   156→    public function store(CrearPlanRequest $request): JsonResponse {
   157→        $codigo    = 201;
   158→        $respuesta = [
   159→            'message' => 'Plan creado correctamente'
   160→        ];
   161→
   162→        try {
   163→            DB::beginTransaction();
   164→            $this->servicio->guardar($request);
   165→            DB::commit();
   166→        } catch (Exception $error) {
   167→            DB::rollBack();
