   242→    /**
   243→     * Archivar o desarchivar plan.
   244→     *
   245→     * @param int $id ID del plan.
   246→     * @param Request $request Contenido de la petición.
   247→     * @return JsonResponse
   248→     * @author Daniel Beltrán
   249→     */
   250→    public function archivar(int $id, Request $request): JsonResponse {
   251→        $codigo    = 200;
   252→        $activo    = $request->filled('activo') ? filter_var($request->activo, FILTER_VALIDATE_BOOLEAN) : false;
   253→        $accion    = $activo ? 'desarchivado' : 'archivado';
   254→        $respuesta = [
   255→            'tipo'    => 'success',
   256→            'mensaje' => "Plan {$accion} correctamente"
   257→        ];
   258→
   259→        $request->merge([
   260→            'id'         => $id,
   261→            'activo'     => $activo,
   262→            'comentario' => $request->filled('comentario') ? $request->comentario : null
   263→        ]);
   264→
   265→        try {
   266→            DB::beginTransaction();
   267→            $this->servicio->guardar($request);
   268→            DB::commit();
   269→        } catch (Exception $error) {
   270→            DB::rollBack();
