   350→    /**
   351→     * Carga la vista que muestra los planes de una empresa dada.
   352→     *
   353→     * @return View
   354→     * @author Fahibram Cárcamo
   355→     * @author Jair Carvajal
   356→     * @author Daniel Beltrán
   357→     */
   358→    public function planesClientes(): View {
   359→        $empresa_id = $this->existeEnSesion('empresa_id') ? $this->obtenerDeSesion('empresa_id') : null;
   360→        $empresa    = $this->empresa_repositorio->getUnoBasico($empresa_id, new Request(['con_usuarios' => true]));
   361→        $sucursales = $this->empresa_repositorio->obtenerSucursales($empresa_id, new Request(['con_usuarios' => true]));
   362→        $empresas   = clone $sucursales;
   363→        $empresas->push($empresa);
   364→        $empresas_ids = array_column($empresas->toArray(), 'id');
   365→
   366→        $planes        = [];
   367→        $tipos_pruebas = $this->tipo_prueba_repositorio->getTodo(new Request());
   368→
   369→        foreach ($tipos_pruebas as $tipo_prueba) {
   370→            $plan = $this->empresa_plan_servicio->obtenerPlanActivo($empresa_id, $tipo_prueba->id)
   371→                ?? $this->empresa_plan_servicio->obtenerUltimoPorTipo($empresa_id, $tipo_prueba->id);
   372→
   373→            if (!is_null($plan)) {
   374→                array_push($planes, $plan);
   375→            }
   376→        }
   377→
   378→        $usuarios = $this->usuario_repositorio->obtenerUsuariosSucursalesEmpresa(
   379→            new Request(['con_rol_tipo_empresa' => true, 'con_relaciones' => true]),
   380→            $empresa
   381→        );
   382→
   383→        return view('empresa.paginas.planes.planes')->with([
   384→            'empresa'          => $empresa,
   385→            'usuario_id'       => Auth::id(),
   386→            'sucursales'       => $empresas,
   387→            'planes'           => $planes,
   388→            'rutas_menu'       => $this->ruta_servicio->obtenerTodas(new Request(['rutas_principales' => true])),
   389→            'perfiles'         => $this->perfil_repositorio->obtenerPerfilesEmpresas($empresas_ids),
   390→            'pruebas'          => $this->prueba_repositorio->getTodo(new Request()),
   391→            'roles'            => $this->rol_repositorio->getTodo(new Request()),
   392→            'cargos'           => $this->cargo_servicio->obtenerCargosEmpresaSucursales($empresa),
   393→            'usuarios'         => $usuarios,
   394→            'servicios_ia'     => $this->servicio_ia_repositorio->getTodo(new Request(['activo' => true])),
   395→            'usuarios_empresa' => $usuarios
   396→        ]);
   397→    }
   398→}
