    28→/**
    29→ * Renderiza la modal de historial de compras de planes de empresa.
    30→ *
    31→ * @param {number} empresa_plan_id Id del registro de la tabla empresas_planes del cual se van a obtener los consumos.
    32→ * @param {string} nombre_plan Nombre del plan.
    33→ * @param {string} tipo_plan Tipo del plan.
    34→ * @param {string|null} nombre_sucursal Nombre de la sucursal.
    35→ * @returns {void}
    36→ * @author Andres Morales
    37→ */
    38→function abrirModalConsumos(empresa_plan_id, nombre_plan, tipo_plan, nombre_sucursal = null) {
    39→    configurarElementosModal(tipo_plan, nombre_plan, nombre_sucursal);
    40→
    41→    empresa_plan = empresas_planes.find(plan => plan.id === empresa_plan_id);
    42→    actualizarBarraProgreso();
    43→
    44→    renderTablasUnidadesPruebas(empresa_plan_id, 'unidades-pruebas');
    45→
    46→    $(`#${MODAL_HISTORIAL_COMPRAS_ID}`).modal('hide');
    47→    $(`#${ID_MODAL_CONSUMOS}`).modal('show');
    48→}
