    59→function configurarElementosModal(tipo_plan, nombre_plan, nombre_sucursal) {
    60→    $('#tipo-plan').text(tipo_plan);
    61→    $('#detalle-consumo-icono-header').removeClass().addClass(`bi ${ICONOS_PLANES_TIPO[tipo_plan]} text-primary me-3`);
    62→    $('#nombre-plan').text(nombre_plan);
    63→
    64→    const $sucursalContainer = $('#sucursal-plan').parent();
    65→    const $nombrePlanContainer = $('#nombre-plan').parent();
    66→
    67→    if (nombre_sucursal) {
    68→        $('#sucursal-plan').text(nombre_sucursal);
    69→        $sucursalContainer.removeClass('d-none');
    70→        $nombrePlanContainer.removeClass('mb-8');
    71→    } else {
    72→        $sucursalContainer.addClass('d-none');
    73→        $nombrePlanContainer.addClass('mb-8');
    74→    }
    75→}
