    77→/**
    78→ * Actualiza la barra de progreso de unidades totales.
    79→ *
    80→ * @returns {void}
    81→ * @author Andres Morales
    82→ */
    83→function actualizarBarraProgreso() {
    84→    const total_unidades = empresa_plan?.conteo_consumos_agendas ?? 0;
    85→    const base_total_unidades = empresa_plan?.unidades ?? 1;
    86→    const porcentaje = (total_unidades / base_total_unidades) * 100;
    87→
    88→    const contenidoHtml = `
    89→        <p class="text-label-2 mb-1">
    90→            <i class="bi bi-box-fill text-headline-5 me-2"></i>Unidades totales
    91→        </p>
    92→        ${crearProgressBar({
    93→            label: `${total_unidades}/${base_total_unidades}`,
    94→            height: '8px',
    95→            porcentaje: { porcentaje, texto: false }
    96→        })}
    97→    `;
    98→
    99→    $('#unidades-pruebas-totales-unidades').empty().append(contenidoHtml);
   100→}
