   102→/**
   103→ * Renderiza la tabla de consumos de unidades de agendas.
   104→ *
   105→ * @param {int} empresa_plan_id Id del registro de la tabla empresas_planes del cual se van a obtener los consumos.
   106→ * @param {string} nombre_tabla Nombre de la tabla.
   107→ * @returns {void}
   108→ * @author Andres Morales
   109→ */
   110→function renderTablasUnidadesPruebas(empresa_plan_id, nombre_tabla) {
   111→    establecerEventosDescargarConsumosExcel(empresa_plan_id, 'unidades_pruebas');
   112→    let nombre_tabla_completo = `tabla-consumos-${nombre_tabla}`;
   113→    destruirTablaExistente(`${nombre_tabla_completo}`);
   114→    $(`#${nombre_tabla_completo}`).DataTable({
   115→        ...configuracion_tablas,
   116→        ajax: {
   117→            url : `/consumos-agendas-empresa-plan`,
   118→            data: function (datos) {
   119→                datos.empresa_plan_id = empresa_plan_id;
   120→                datos.solo_con_agendas = true;
   121→                (json_filtros.pruebas_filtro) ? datos.prueba_id = json_filtros.pruebas_filtro : '';
   122→                (json_filtros.perfiles_filtro) ? datos.perfil_id = json_filtros.perfiles_filtro : '';
   123→                (json_filtros.creador_filtro) ? datos.creador_id = json_filtros.creador_filtro : '';
   124→                (json_filtros.sucursales_filtro) ? datos.sucursal_id = json_filtros.sucursales_filtro : '';
   125→                if (typeof json_filtros.fecha_consumo_filtro === 'string') {
   126→                    const partesFecha = json_filtros.fecha_consumo_filtro.split(' a ');
   127→                    (partesFecha[0]) ? datos.fecha_creacion_desde = partesFecha[0] : '';
   128→                    (partesFecha[1]) ? datos.fecha_creacion_hasta = partesFecha[1] : '';
   129→                }
