   490→/**
   491→ * Selecciona o deselecciona un participante
   492→ *
   493→ * @param {boolean} seleccion Selección.
   494→ * @param {string} fila_id Id de la fila de la tabla.
   495→ * @param {string} tabla_consumos_id Id del elemento html de la tabla de consumos.
   496→ * @param {string} nombre_tabla Nombre de la tabla.
   497→ * @returns {void}
   498→ * @author Andres Morales
   499→ */
   500→function seleccionarConsumo(seleccion, fila_id, tabla_consumos_id, nombre_tabla) {
   501→    const id_num = parseInt(fila_id);
   502→
   503→    if (!filas_seleccionadas_ids[nombre_tabla]) {
   504→        filas_seleccionadas_ids[nombre_tabla] = [];
   505→    }
   506→    if (!ids_filas[nombre_tabla]) {
   507→        ids_filas[nombre_tabla] = [];
   508→    }
   509→
   510→    if (seleccion) {
   511→        if (!filas_seleccionadas_ids[nombre_tabla].includes(id_num)) {
   512→            filas_seleccionadas_ids[nombre_tabla].push(id_num);
   513→        }
   514→    } else {
   515→        filas_seleccionadas_ids[nombre_tabla] = filas_seleccionadas_ids[nombre_tabla].filter(id => id !== id_num);
   516→    }
   517→
   518→    validarSeleccion(nombre_tabla, tabla_consumos_id);
   519→}
