   529→function validarSeleccion(nombre_tabla, tabla_consumos_id) {
   530→    if (!filas_seleccionadas_ids[nombre_tabla]) {
   531→        filas_seleccionadas_ids[nombre_tabla] = [];
   532→    }
   533→    if (!ids_filas[nombre_tabla]) {
   534→        ids_filas[nombre_tabla] = [];
   535→    }
   536→
   537→    const seleccionados = filas_seleccionadas_ids[nombre_tabla].length;
   538→    const total = ids_filas[nombre_tabla].length;
   539→    const checkboxTodos = $(`#checkbox-seleccionar-todos-${tabla_consumos_id}`);
   540→
   541→    if (seleccionados === 0) {
   542→        checkboxTodos.prop('indeterminate', false);
   543→        checkboxTodos.prop('checked', false);
   544→    } else if (seleccionados === total && total > 0) {
   545→        checkboxTodos.prop('indeterminate', false);
   546→        checkboxTodos.prop('checked', true);
   547→    } else {
   548→        checkboxTodos.prop('indeterminate', true);
