   561→function establecerEventosDescargarConsumosExcel(empresa_plan_id, nombre_tabla) {
   562→    let ruta_descarga = {
   563→        unidades_pruebas: 'consumos-plan-prueba-exportar',
   564→        ofertas: 'consumos-plan-oferta-laboral-exportar',
   565→        usuarios: 'consumos-plan-usuarios-exportar'
   566→    }[nombre_tabla];
   567→
   568→    $(`#boton-descargar-${ids_tablas_consumos[nombre_tabla]}`).off('click').on('click', () => {
   569→        let filtro_fecha = $(`#filtro_rango_${nombre_tabla}`).val();
   570→
   571→        fecha_inicio_creacion = '';
   572→        fecha_fin_creacion = '';
   573→
   574→        let consumos_ids = filas_seleccionadas_ids[nombre_tabla].length ? [...filas_seleccionadas_ids[nombre_tabla]] : [];
   575→        let parametros = consumos_ids.length ? { ids_consumos: consumos_ids, empresa_plan_id } : { empresa_plan_id };
   576→        if (nombre_tabla === 'usuarios') parametros.empresa_id = $('#input_empresa_id').val();
   577→        if (filtro_fecha) {
   578→            let fechas = filtro_fecha.split(" a ");
   579→            
   580→            fecha_inicio_creacion = fechas[0];
   581→            fecha_fin_creacion = fechas[0];
   582→            if (fechas[1]) {
   583→                fecha_fin_creacion = fechas[1];
   584→            }
   585→            parametros.fecha_inicio = fecha_inicio_creacion;
   586→            parametros.fecha_fin = fecha_fin_creacion;
   587→        }        
   588→        descargarExcel(ruta_descarga, `consumos_${nombre_tabla}`, true, parametros);
   589→    });
   590→}
