   185→/**
   186→ * Seleccionar o deseleccionar todos los consumos de créditos.
   187→ *
   188→ * @param {boolean} seleccion Selección.
   189→ * @returns {void}
   190→ * @author Jair Carvajal
   191→ * @author Esperanza Olivo
   192→ */
   193→function seleccionarTodosConsumosCreditos(seleccion) {
   194→    filas_seleccionadas_creditos = [];
   195→    if (seleccion) {
   196→        filas_seleccionadas_creditos = [...filas_creditos];
   197→    }
   198→    actualizarDatatables('tabla-consumos-creditos');
   199→}
   200→
   201→/**
   202→ * Selecciona o deselecciona un consumo de crédito.
   203→ *
   204→ * @param {boolean} seleccion Selección.
   205→ * @param {number} fila_id Id de la fila de la tabla.
   206→ * @returns {void}
   207→ * @author Jair Carvajal
   208→ */
   209→function seleccionarConsumoCredito(seleccion, fila_id) {
   210→    const NUMERO_ID = parseInt(fila_id);
   211→
   212→    if (seleccion) {
   213→        if (!filas_seleccionadas_creditos.includes(NUMERO_ID)) {
   214→            filas_seleccionadas_creditos.push(NUMERO_ID);
   215→        }
   216→    } else {
   217→        filas_seleccionadas_creditos = filas_seleccionadas_creditos.filter(id => id !== NUMERO_ID);
   218→    }
   219→
   220→    validarSeleccionCreditos();
   221→}
   222→
   223→/**
   224→ * Validar los consumos de créditos seleccionados.
   225→ *
   226→ * @returns {void}
   227→ * @author Jair Carvajal
   228→ * @author Esperanza Olivo
   229→ */
   230→function validarSeleccionCreditos() {
   231→    let seleccionados = filas_seleccionadas_creditos.length;
   232→    let total = filas_creditos.length;
   233→    let seleccionado_todos = $('#checkbox-seleccionar-todos-tabla-consumos-creditos');
   234→
   235→    if (seleccionados === 0) {
   236→        seleccionado_todos.prop('indeterminate', false);
   237→        seleccionado_todos.prop('checked', false);
   238→    } else if (seleccionados === total && total > 0) {
   239→        seleccionado_todos.prop('indeterminate', false);
   240→        seleccionado_todos.prop('checked', true);
   241→    } else {
   242→        seleccionado_todos.prop('indeterminate', true);
   243→    }
   244→}
   245→
   246→/**
   247→ * Aplica los filtros del dropdown de filtros avanzados.
   248→ * Esta función es llamada automáticamente por el componente crearFiltroDropdown.
   249→ *
   250→ * @param {string} campo_id Id del filtro dropdown.
   251→ * @param {object} filtros Objeto con los filtros aplicados.
   252→ * @returns {void}
   253→ * @author Jair Carvajal
   254→ */
   255→function aplicarFiltrosDropdownConsumosCreditos(campo_id) {
   256→    if (campo_id === 'filtro_empresas') {
   257→        actualizarDatatables('tabla-consumos-creditos');
   258→    }
   259→}
   260→
   261→window.__aplicarFiltrosDropdownHandlers = window.__aplicarFiltrosDropdownHandlers || [];
   262→window.__aplicarFiltrosDropdownHandlers.push(aplicarFiltrosDropdownConsumosCreditos);
   263→
   264→if (!window.aplicarFiltrosDropdown || !window.aplicarFiltrosDropdown.__esDispatcher) {
   265→    window.aplicarFiltrosDropdown = function (filtro, filtros) {
   266→        (window.__aplicarFiltrosDropdownHandlers || []).forEach(handler => handler(filtro, filtros));
   267→    };
   268→    window.aplicarFiltrosDropdown.__esDispatcher = true;
   269→}
