     1→/**
     2→ * Crear una tabla
     3→ * 
     4→ * @param {object} componente Componente de tablas.
     5→ * @returns {string} Componente de tabla.
     6→ * @author José Manuel Jiménez
     7→ * @author Andres Morales
     8→ * @author Jair Carvajal
     9→ * @author Fahibram Cárcamo
    10→ */
    11→function crearTabla(componente) {    
    12→    return `
    13→        <div class="${componente.class_general ? componente.class_general : ''} row justify-content-end d-none tabla-componente-${componente.id}">
    14→            ${componente.buscador ? `
    15→                <div class="col-12 col-md-auto pe-md-0 me-md-3 my-auto ${componente.buscador.icono3 ? 'position-relative buscador-tabla' : ''}">
    16→                    <div id="buscador-${componente.id}" class="input-container my-auto ${componente.input_container_class ? componente.input_container_class : ''} ${componente.buscador.icono || componente.buscador.icono2 || componente.buscador.icono3 ? 'input-icon' : ''}">
    17→                        ${componente.buscador.icono ? componente.buscador.icono : ''}
    18→                        ${componente.buscador.icono2 ? componente.buscador.icono2 : ''}
    19→                    </div>
    20→                    ${componente.buscador.icono3 ? componente.buscador.icono3 : ''}
    21→                </div>
    22→            ` : ''}
    23→            ${componente.filtro ? `
    24→                <div class="col-12 col-xl-auto my-auto ${componente.buscador_datatable ? 'px-0': ''}">
    25→                    ${componente.filtro}
    26→                </div>
    27→            ` : ''}
    28→            <div class="col-12 col-lg my-auto">
    29→                <div class="row my-auto ${componente.botones_class ? componente.botones_class : 'justify-content-end'}">
    30→                    ${componente.botones ? componente.botones : ''}
    31→                </div>
    32→            </div>
    33→            ${componente.chips ? componente.chips : ''}
    34→            ${componente.subtitulo ? componente.subtitulo : ''}
    35→        </div>
    36→        <div class="${componente.class_tabla ? componente.class_tabla : ''} table-responsive w-100 d-none tabla-componente-${componente.id}">
    37→            <table
    38→                id="${componente.id}"
    39→                class="table w-100 tabla-componente ${componente.class ? componente.class : ''}"
    40→                data-url="${componente.url}"
    41→                data-registros_paginas="${componente.registros_paginas ? componente.registros_paginas : false}"
    42→                data-buscador="${componente.buscador ? componente.buscador : false}"
    43→                data-issubtabla="${componente.issubtabla ? componente.issubtabla : false}"
    44→            >
    45→                <thead class="thead-componente">
    46→                    ${componente.thead ? componente.thead : ''}
    47→                </thead>
    48→                <thead></thead>
    49→                <tbody></tbody>
    50→            </table>
    51→        </div>
    52→    `;
    53→}
