    67→/**
    68→ * Renderiza todas las tablas aplicando los estilos de datatables.
    69→ *  
    70→ * @returns {void} No retorna nada.
    71→ * @author José Manuel Jiménez
    72→ * @author Andres Morales
    73→ */
    74→function cargarDataTablas() {
    75→    let tablas = $('.tabla-componente');
    76→    $.each(tablas, function(index, tabla) {
    77→        let tabla_id = $(tabla).attr('id');
    78→        let registros_paginas = $(tabla).data('registros_paginas');
    79→        let buscador_tabla = $(tabla).data('buscador');
    80→        let issubtabla = $(tabla).data('issubtabla');
    81→        
    82→        $(`#${tabla_id} .input-filtro`).on('keydown', function(event) {
    83→            if (event.key === 'Enter') {
    84→                actualizarDatatables(tabla_id);
    85→            }
    86→        });
    87→        $(`#${tabla_id} .input-filtro`).on('blur', function() {
    88→            actualizarDatatables(tabla_id);
    89→        });
    90→        $(`#${tabla_id} .select-filtro`).on('change', function() {
    91→            actualizarDatatables(tabla_id);
    92→        });
    93→        if (registros_paginas) {
    94→            let numero_registros = $(`#${tabla_id}_wrapper .dt-input`).first();
    95→            $(numero_registros).addClass('select2 rounded-2 text-body-2 w-90px');
    96→            $(numero_registros).prop('id', `numero-registros-${tabla_id}`);
    97→            $(`#${tabla_id}_info`).after(`<div class="contenedor-numero-registros" id="contenedor-numero-registros-${tabla_id}"</div>`);
    98→            $(`#contenedor-numero-registros-${tabla_id}`).append(numero_registros);
    99→            $(`#${tabla_id}_wrapper .dt-length`).remove();
   100→        } else {
   101→            $(`#${tabla_id}_wrapper .dt-info`).remove();
   102→            $(`#${tabla_id}_wrapper .dt-length`).remove();
   103→        }
   104→        if (buscador_tabla) {
   105→            
   106→            let buscador = $(`#${tabla_id}_wrapper input.dt-input`);
   107→            $(buscador).addClass('rounded-2 text-body-2 w-100');
   108→            $(buscador).prop('placeholder', 'Buscar');
   109→            $(buscador).attr('aria-controls', tabla_id);
   110→            $(buscador).css('padding-right', '52px');
   111→            $(`#${tabla_id}_filter`).remove();
   112→            $(`#buscador-${tabla_id}`).append(buscador);
   113→            $(`#${tabla_id}_wrapper .dt-search`).remove();
   114→            setTimeout(() => {
   115→                $(`#buscador-${tabla_id} input[type="search"]::-webkit-search-cancel-button`).css({
   116→                    'margin-right': '30px'
   117→                });
   118→            }, 100);
   119→            $(`#buscador-${tabla_id} input.dt-input:not(:last)`).remove();
   120→        } else {
   121→            $(`#${tabla_id}_wrapper .dt-search`).remove();
   122→        }
   123→        $(`.tabla-componente-${tabla_id}`).removeClass('d-none');
   124→        $(`.tabla-componente-${tabla_id} thead`).removeClass('d-none');
   125→        $(`#${tabla_id} > .thead-componente`).addClass('d-none');
   126→        
   127→        if (issubtabla) {
   128→            $(`#${tabla_id} tbody`).off('click', issubtabla).on('click', issubtabla, function() {
   129→                let table = $(`#${tabla_id}`).DataTable();
   130→                let tr = $(this).closest('tr');
   131→                let row = table.row(tr);
   132→                
   133→                if (row.child.isShown()) {
   134→                    row.child.hide();
   135→                    tr.removeClass('shown');
   136→                    $(this).html('<i class="bi bi-chevron-down"></i>');
   137→                } else {
   138→                    row.child(crearContenidoSubTabla(row.data())).show();
   139→                    tr.addClass('shown');
   140→                    $(this).html('<i class="bi bi-chevron-up"></i>');
   141→                }
   142→            });
   143→        }
   144→        setTimeout(() => {             
   145→            ajustarRenderDataTable(tabla_id);
   146→        }, 500);
   147→    });
   148→    actualizarSelect2();
   149→}
