    78→    public function guardar(Request $request): EmpresaPlan {
    79→        $tiene_plan_seleccion = $request->filled('plan_id');
    80→        $tiene_plan_creditos  = $request->filled('plan_credito_id');
    81→        $plan_asignado        = null;
    82→
    83→        // Si tiene plan de selección, crear/actualizar ese plan
    84→        if ($tiene_plan_seleccion) {
    85→            $plan_asignado = $this->guardarPlanSeleccion($request);
    86→        }
    87→
    88→        // Si tiene plan de créditos, crear ese plan por separado
    89→        if ($tiene_plan_creditos) {
    90→            $plan_creditos = $this->guardarPlanCreditos($request);
    91→
    92→            // Si no había plan de selección, retornar el de créditos
    93→            if (!$tiene_plan_seleccion) {
