   101→    /**
   102→     * Guardar un plan de selección.
   103→     *
   104→     * @param Request $request Contenido de la petición.
   105→     * @return EmpresaPlan
   106→     * @author Andres Gonzalez
   107→     */
   108→    protected function guardarPlanSeleccion(Request $request): EmpresaPlan {
   109→        $enviar_correo    = false;
   110→        $campos_excluidos = ['_token', '_method', 'id', 'tipo_prueba_id', 'factura', 'vigencia', 'pruebas', 'funcionalidades', 'precio_creditos', 'porcentaje_descuento_creditos', 'plan_credito_id', 'creditos'];
   111→        $plan             = $this->plan_repositorio->getUno($request->plan_id);
   112→        $plan_activo      = $this->repositorio->planActivo($request->empresa_id, $request->tipo_prueba_id);
   113→        $vigencia         = $plan->vigencia;
   114→
   115→        if ($request->filled('vigencia')) {
   116→            $vigencia = $request->vigencia;
   117→        }
   118→
   119→        if (!is_null($plan_activo)) {
   120→            if ($request->filled('id')) {
   121→                // Si el ID recibido es igual al plan activo, no se calcula vigencia
   122→                if ($request->id === $plan_activo->id) {
   123→                    goto omitir_calcular_vigencia_seleccion;
   124→                }
   125→
   126→                // Si la vigencia no ha sido modificada respecto al plan base, no se calcula vigencia
   127→                if ($vigencia == $request->vigencia) {
   128→                    array_push($campos_excluidos, 'fecha_inicio', 'fecha_vencimiento');
   129→                    goto omitir_calcular_vigencia_seleccion;
   130→                }
   131→            }
   132→
   133→            // Poner el nuevo plan como proximo
   134→            $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   135→
   136→            // Validar si el plan activo tiene fecha de vencimiento y el plan seleccionado tiene vigencia
   137→            if (!is_null($plan_activo->fecha_vencimiento) && !is_null($plan->vigencia)) {
   138→                // Agregar las nuevas fechas al nuevo plan
   139→                $hoy          = now();
   140→                $fecha_inicio = ($hoy > $plan_activo->fecha_vencimiento) ? $hoy->copy() : Carbon::createFromDate($plan_activo->fecha_vencimiento)->addDay();
   141→                $request->merge($this->calcularFechasVigencia($vigencia, $fecha_inicio));
   142→            }
   143→        } else {
   144→            $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   145→
   146→            // Validar si el plan seleccionado tiene vigencia
   147→            if (!is_null($plan->vigencia)) {
   148→                $request->merge($this->calcularFechasVigencia($vigencia));
   149→            }
   150→        }
   151→
   152→        omitir_calcular_vigencia_seleccion:
   153→
   154→        if (!$request->filled('unidades')) {
   155→            $request->merge(['unidades' => $plan->unidades]);
   156→        } elseif ($request->unidades != $plan->unidades) {
   157→            $request->merge(['plan_original' => false]);
   158→        }
   159→
   160→        if (!$request->filled('cantidad_ofertas_laborales')) {
   161→            $request->merge(['cantidad_ofertas_laborales' => $plan->cantidad_ofertas_laborales]);
   162→        } elseif ($request->cantidad_ofertas_laborales != $plan->cantidad_ofertas_laborales) {
   163→            $request->merge(['plan_original' => false]);
   164→        }
   165→
   166→        if (!$request->filled('cantidad_usuarios')) {
   167→            $request->merge(['cantidad_usuarios' => $plan->cantidad_usuarios]);
   168→        } elseif ($request->cantidad_usuarios != $plan->cantidad_usuarios) {
   169→            $request->merge(['plan_original' => false]);
   170→        }
   171→
   172→        if (!$request->filled('cantidad_pruebas')) {
   173→            $request->merge(['cantidad_pruebas' => $plan->cantidad_pruebas]);
   174→        } elseif ($request->cantidad_pruebas != $plan->cantidad_pruebas) {
   175→            $request->merge(['plan_original' => false]);
   176→        }
   177→
   178→        // Comprobar si se modificaron las funcionalidades respecto al plan original
   179→        if (!is_null($plan->funcionalidades) && $request->filled('funcionalidades')) {
   180→            $funcionalidades_originales = $plan->funcionalidades->pluck('id')->toArray();
   181→            $primera_comparacion        = array_diff($request->funcionalidades, $funcionalidades_originales);
   182→            $segunda_comparacion        = array_diff($funcionalidades_originales, $request->funcionalidades);
   183→
   184→            if (count([...$primera_comparacion, ...$segunda_comparacion]) > 0) {
   185→                $request->merge(['plan_original' => false]);
   186→            }
   187→        }
   188→
   189→        if ($request->filled('id')) {
   190→            // Eliminar la factura actual
   191→            if ($request->hasFile('factura')) {
   192→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   193→
   194→                if (!is_null($archivo)) {
   195→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   196→                    $this->archivo_repositorio->eliminar($archivo->id);
   197→                }
   198→            }
   199→
   200→            $plan_asignado = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
   201→        } else {
   202→            $enviar_correo = true;
   203→            $request->merge(['usuario_id' => auth()->user()->id]);
   204→            $plan_asignado = $this->repositorio->crear($request->except($campos_excluidos));
   205→        }
   206→
   207→        if ($request->filled('pruebas')) {
   208→            $this->repositorio->sincronizarPruebas($plan_asignado, $request->pruebas);
   209→        }
   210→
   211→        if ($request->filled('funcionalidades')) {
   212→            $this->repositorio->sincronizarFuncionalidades($plan_asignado, $request->funcionalidades);
   213→        }
   214→
   215→        if ($request->hasFile('factura')) {
   216→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   217→        }
   218→
   219→        if ($enviar_correo) {
   220→            $empresa      = $this->empresa_repositorio->getUno($request->empresa_id);
   221→            $usuario      = $empresa->usuarios[0];
   222→            $empresa_plan = $this->repositorio->getUno($plan_asignado->id);
   223→            $titulo       = 'Usuario principal de la empresa creado';
   224→            Mail::send(new CrearUsuarioMail($usuario, Password::createToken($usuario), $titulo, $titulo, '', $empresa, $empresa_plan));
   225→        }
   226→
   227→        return $plan_asignado;
   228→    }
