   230→    /**
   231→     * Guardar un plan de créditos.
   232→     *
   233→     * @param Request $request Contenido de la petición.
   234→     * @return EmpresaPlan
   235→     * @author Andres Gonzalez
   236→     */
   237→    protected function guardarPlanCreditos(Request $request): EmpresaPlan {
   238→        $enviar_correo = false;
   239→        $plan_credito  = $this->plan_credito_repositorio->getUno($request->plan_credito_id);
   240→        $plan_activo   = $this->repositorio->planCreditosActivo($request->empresa_id);
   241→
   242→        // Preparar datos para el plan de créditos
   243→        $datos_creditos = [
   244→            'empresa_id'           => $request->empresa_id,
   245→            'plan_id'              => null,
   246→            'plan_credito_id'      => $request->plan_credito_id,
   247→            'creditos'             => $plan_credito->creditos ?? 0,
   248→            'precio'               => $request->precio_creditos ?? $plan_credito->precio ?? null,
   249→            'porcentaje_descuento' => $request->porcentaje_descuento_creditos ?? null,
   250→            'numero_factura'       => $request->numero_factura,
   251→            'comentario'           => $request->comentario,
   252→            'fecha_inicio'         => null,
   253→            'fecha_vencimiento'    => null,
   254→            'usuario_id'           => auth()->user()->id,
   255→        ];
   256→
   257→        // Si es edición de un plan de créditos existente
   258→        if ($request->filled('id')) {
   259→            // Eliminar la factura actual si se sube una nueva
   260→            if ($request->hasFile('factura')) {
   261→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   262→
   263→                if (!is_null($archivo)) {
   264→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   265→                    $this->archivo_repositorio->eliminar($archivo->id);
   266→                }
   267→            }
   268→
   269→            $plan_asignado = $this->repositorio->actualizar($request->id, $datos_creditos);
   270→        } else {
   271→            // Determinar estado para nuevo plan
   272→            if (!is_null($plan_activo) && $plan_activo->id != $request->id) {
   273→                $datos_creditos['estado_id'] = Plan::ESTADO_PROXIMO_ID;
   274→            } else {
   275→                $datos_creditos['estado_id'] = Plan::ESTADO_ACTIVO_ID;
   276→            }
   277→
   278→            $enviar_correo = true;
   279→            $plan_asignado = $this->repositorio->crear($datos_creditos);
   280→        }
   281→
   282→        if ($request->hasFile('factura')) {
   283→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   284→        }
   285→
   286→        return $plan_asignado;
   287→    }
