   289→    /**
   290→     * Renovar el plan asignado.
   291→     *
   292→     * @param int $id ID del plan de la empresa que se quiere renovar.
   293→     * @throws Exception Si el plan que se quiere renovar está en estado 'próximo'.
   294→     * @return EmpresaPlan Plan de la empresa renovado.
   295→     * @author Daniel Beltrán
   296→     */
   297→    public function renovar(int $id): EmpresaPlan {
   298→        $empresa_plan = $this->repositorio->getUno($id);
   299→
   300→        if ($empresa_plan->estado_id === Plan::ESTADO_PROXIMO_ID) {
   301→            throw new Exception('No se puede renovar un plan que no ha entrado en vigencia');
   302→        }
   303→
   304→        $request = new Request(Arr::except($empresa_plan->toArray(), ['id', 'renovando', 'por_facturar', 'plan', 'estado', 'vigencia_dias']));
   305→
   306→        $request->merge(['por_facturar' => true]);
   307→
   308→        if (in_array($empresa_plan->estado_id, [Plan::ESTADO_ACTIVO_ID, Plan::ESTADO_POR_VENCER_ID])) {
   309→            // Cuando el plan actual está activo o por vencer, se asigna el estado 'próximo'
   310→            $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   311→        } elseif (in_array($empresa_plan->estado_id, [Plan::ESTADO_INACTIVO_ID, Plan::ESTADO_VENCIDO_ID])) {
   312→            // Cuando el plan actual está inactivo o vencido, se asigna el estado 'activo'
   313→            $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   314→        }
   315→
   316→        $this->verificarYAsignarTiempo($empresa_plan, $request);
   317→
   318→        $plan_renovado = $this->repositorio->crear($request->toArray());
   319→
   320→        if ($request->filled('pruebas')) {
   321→            $this->repositorio->sincronizarPruebas($plan_renovado, $request->pruebas);
   322→        }
   323→
   324→        if ($request->filled('funcionalidades')) {
   325→            $this->repositorio->sincronizarFuncionalidades($plan_renovado, $request->funcionalidades);
   326→        }
   327→
   328→        return $plan_renovado;
   329→    }
