   345→    /**
   346→     * Calcular las fechas en las que el plan estará vigente.
   347→     *
   348→     * @param int $vigencia Cantidad de días de vigencia.
   349→     * @param Carbon|null $fecha_inicio_vigencia Fecha en la que inicia la vigencia.
   350→     * @return array Fechas de inicio y vencimiento.
   351→     * @author Daniel Beltrán
   352→     */
   353→    public function calcularFechasVigencia(int $vigencia, ?Carbon $fecha_inicio_vigencia = null): array {
   354→        $hoy = now();
   355→
   356→        $fecha_inicio = !is_null($fecha_inicio_vigencia) ? $fecha_inicio_vigencia : $hoy;
   357→
   358→        // Si es múltiplo de 3, entonces la vigencia es en meses
   359→
   360→        if (($vigencia % 3) === 0 && gettype($vigencia / 30) === 'integer') {
   361→            $meses = $vigencia / 30;
   362→
   363→            $fecha_vencimiento = $fecha_inicio->copy()->addMonths($meses);
   364→
   365→            if ($fecha_inicio->day === 1) {
   366→                $fecha_vencimiento->subDay();
   367→            }
   368→        } else {
   369→            $vigencia = $vigencia < 0 ? abs($vigencia) : $vigencia; // Convierte los valores negativos a positivos
   370→
   371→            $fecha_vencimiento = $fecha_inicio->copy()->addDays($vigencia);
   372→        }
   373→
   374→        return [
   375→            'fecha_inicio' => $fecha_inicio->format('Y-m-d'),
   376→
   377→            'fecha_vencimiento' => $fecha_vencimiento->format('Y-m-d')
   378→        ];
   379→    }
