    21→use App\Http\Repositories\Empresas\{EmpresaPlanRepository, EmpresaRepository, EmpresaUsuarioRepository};
    22→
    23→/**
    24→ * @property ConsumoRepository $repositorio
    25→ */
    26→class ConsumoService extends BaseService
    27→{
    28→    use SesionTrait;
    29→
    30→    private $modelo = Consumo::class;
    31→
    32→    /**
    33→     * Constructor.
    34→     *
    35→     * @param ConsumoRepository $repositorio Repositorio de consumos.
    36→     * @param EmpresaRepository $empresa_repositorio Repositorio de empresas.
    37→     * @param UserRepository $usuario_repositorio Repositorio de usuarios.
    38→     * @param TipoPruebaRepository $tipo_prueba_repositorio Repositorio de tipos pruebas.
    39→     * @param EmpresaPlanRepository $empresa_plan_repositorio Repositorio de empresas planes.
    40→     * @param EmpresaPlanService $empresa_plan_servicio Servicio de empresas planes.
    41→     * @param OfertaLaboralRepository $oferta_laboral_repositorio Repositorio de ofertas laborales.
    42→     * @param AgendaRepository $agenda_repositorio Repositorio de agendas.
    43→     * @param EmpresaUsuarioRepository $empresa_usuario_repositorio Repositorio de empresas usuarios.
    44→     * @return void
    45→     * @author Fahibram Cárcamo
    46→     */
    47→    public function __construct(
    48→        ConsumoRepository $repositorio,
    49→        private EmpresaRepository $empresa_repositorio,
    50→        private UserRepository $usuario_repositorio,
    51→        private TipoPruebaRepository $tipo_prueba_repositorio,
    52→        private EmpresaPlanRepository $empresa_plan_repositorio,
    53→        private EmpresaPlanService $empresa_plan_servicio,
    54→        private OfertaLaboralRepository $oferta_laboral_repositorio,
    55→        private AgendaRepository $agenda_repositorio,
    56→        private EmpresaUsuarioRepository $empresa_usuario_repositorio,
    57→        private VacanteRepository $vacante_repositorio
    58→    ) {
    59→        parent::__construct($repositorio);
    60→    }
    61→
    62→    /**
    63→     * Obtener los usuarios de los consumos de un registro de la tabla empresas_planes.
    64→     *
    65→     * @param Request $request Solicitud HTTP.
    66→     * @return Collection
    67→     * @author Fahibram Cárcamo
    68→     */
    69→    public function obtenerUsuariosConsumosPlan(Request $request): Collection {
    70→        $consumos = $this->repositorio->getTodo(new Request([
    71→            'empresa_plan_id'       => $request->empresa_plan_id,
    72→            'con_agendas'           => true,
    73→            'con_ofertas_laborales' => true,
    74→            'con_empresa'           => true,
    75→            'en'                    => true,
    76→            'campo'                 => 'id',
    77→            'valores'               => $request->ids_consumos
    78→        ]));
    79→
    80→        $empresa  = $this->empresa_repositorio->getUnoBasico($this->obtenerDeSesion('empresa_id'));
    81→        $usuarios = $this->usuario_repositorio->obtenerUsuariosSucursalesEmpresa(new Request(['con_rol_tipo_empresa' => true, 'con_relaciones' => true]), $empresa);
    82→
    83→        $usuarios_salida = collect();
    84→
    85→        foreach ($consumos as $consumo) {
    86→            $usuario            = null;
    87→            $usuarios_filtrados = [];
    88→
    89→            if ($consumo->agenda_id !== null) {
    90→                $usuarios_filtrados = $usuarios->filter(fn($usuario) => $usuario->id === $consumo->agenda->evaluador_id);
    91→            } elseif ($consumo->oferta_laboral_id !== null) {
    92→                $usuarios_filtrados = $usuarios->filter(fn($usuario) => $usuario->id === $consumo->ofertaLaboral->usuario_id);
    93→            }
    94→
    95→            if (count($usuarios_filtrados)) {
    96→                $usuario                = clone $usuarios_filtrados->first();
    97→                $empresa_usuario        = $usuario->empresasUsuarios->filter(fn($empresa_usuario) => $empresa_usuario->empresa_id === $consumo->empresa_id)->first();
    98→                $usuario->rol           = $empresa_usuario->rol;
    99→                $usuario->fecha_consumo = $consumo->created_at;
   100→                $usuario->empresa       = $consumo->empresa;
   101→                $usuarios_salida->push($usuario);
   102→            }
   103→        }
   104→
   105→        return $usuarios_salida;
   106→    }
   107→
   108→    /**
   109→     * Crear el consumo correspondiente al procesamiento de una agenda.
   110→     *
   111→     * @param Agenda|int $agenda Id o instancia de la nueva agenda registrada.
   112→     * @return ?Consumo
   113→     * @author Fahibram Cárcamo
   114→     */
   115→    public function registrarConsumoAgenda(int|Agenda $agenda): ?Consumo {
   116→        $agenda              = gettype($agenda) === 'integer' ? $this->agenda_repositorio->getUno($agenda) : $agenda;
   117→        $empresa_plan_activo = $this->empresa_plan_repositorio->planActivo($agenda->empresa_id, TipoPrueba::SELECCION_ID);
   118→
   119→        if ($this->empresa_plan_servicio->verificarVigenciaPlan($empresa_plan_activo)) {
   120→            $this->crearConsumoModelo($agenda->empresa_id, $empresa_plan_activo->id, 'agenda_id', $agenda->id);
   121→        } elseif (
   122→            !$this->empresa_plan_servicio->validarDisponibilidadUnidades($empresa_plan_activo) &&
   123→            !$this->empresa_plan_servicio->validarDisponibilidadOfertasLaborales($empresa_plan_activo)
   124→        ) {
   125→            $this->empresa_plan_repositorio->actualizar($empresa_plan_activo->id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   126→        }
   127→
   128→        return null;
   129→    }
   130→
   131→    /**
   132→     * Crear el consumo correspondiente a la creación de una nueva oferta laboral.
   133→     *
   134→     * @param int|OfertaLaboral $oferta_laboral Id o instancia de la nueva oferta laboral registrada.
   135→     * @return ?Consumo
   136→     * @author Fahibram Cárcamo
   137→     */
   138→    public function registrarConsumoOfertaLaboral(int|OfertaLaboral $oferta_laboral): ?Consumo {
   139→        $oferta_laboral      = gettype($oferta_laboral) === 'integer' ? $this->oferta_laboral_repositorio->getUno($oferta_laboral) : $oferta_laboral;
   140→        $empresa_plan_activo = $this->empresa_plan_repositorio->planActivo($oferta_laboral->empresa_id, TipoPrueba::SELECCION_ID);
   141→
   142→        if ($this->empresa_plan_servicio->verificarVigenciaPlanOfertasLaborales($empresa_plan_activo)) {
   143→            $this->crearConsumoModelo($oferta_laboral->empresa_id, $empresa_plan_activo->id, 'oferta_laboral_id', $oferta_laboral->id);
   144→        } elseif (
   145→            !$this->empresa_plan_servicio->validarDisponibilidadUnidades($empresa_plan_activo) &&
   146→            !$this->empresa_plan_servicio->validarDisponibilidadOfertasLaborales($empresa_plan_activo)
   147→        ) {
   148→            $this->empresa_plan_repositorio->actualizar($empresa_plan_activo->id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   149→        }
   150→
   151→        return null;
   152→    }
   153→
   154→    /**
   155→    * Crear el consumo correspondiente a la creación de una nueva vacante.
   156→    *
   157→    * @param int|Vacante $vacante  Id o instancia de la nueva vacante registrada.
   158→    * @return ?Consumo  Retorna el registro de consumo creado, o null si no se generó.
   159→    * @author Andres Gonzalez
   160→    */
   161→    public function registrarConsumoVacante(int|Vacante $vacante): ?Consumo {
   162→        // Si llega un ID, obtener la instancia del modelo desde el repositorio
   163→        $vacante = gettype($vacante) === 'integer' ? $this->vacante_repositorio->getUno($vacante) : $vacante;
   164→
   165→        // Buscar el plan activo de la empresa asociada a la vacante
   166→        $empresa_plan_activo = $this->empresa_plan_repositorio->planActivo($vacante->empresa_id, TipoPrueba::SELECCION_ID);
   167→
   168→        // Validar vigencia del plan
   169→        if ($this->empresa_plan_servicio->verificarVigenciaPlanOfertasLaborales($empresa_plan_activo)) {
   170→            // Registrar consumo asociado a la vacante
   171→            $this->crearConsumoModelo($vacante->empresa_id, $empresa_plan_activo->id, 'vacante_id', $vacante->id);
   172→        } elseif (
   173→            !$this->empresa_plan_servicio->validarDisponibilidadUnidades($empresa_plan_activo) &&
   174→            !$this->empresa_plan_servicio->validarDisponibilidadOfertasLaborales($empresa_plan_activo)
   175→        ) {
   176→            // Marcar el plan como vencido si no hay unidades disponibles
   177→            $this->empresa_plan_repositorio->actualizar($empresa_plan_activo->id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   178→        }
   179→
   180→        return null;
   181→    }
   182→
   183→    /**
   184→     * Crear un nuevo consumo según el modelo consumido (oferta laboral o agenda).
   185→     *
   186→     * @param int $empresa_id Id de la empresa del consumo.
   187→     * @param int $empresa_plan_id Id del registro de la tabla empresas_planes.
   188→     * @param string $llave_foranea Llave foránea del modelo relacionado.
   189→     * @param int $valor_llave_foranea Valor de la llave foránea del modelo relacionado.
   190→     * @return Consumo
   191→     * @author Fahibram Cárcamo
   192→     */
   193→    private function crearConsumoModelo(int $empresa_id, int $empresa_plan_id, string $llave_foranea, int $valor_llave_foranea): Consumo {
   194→        return $this->repositorio->crearSiNoExiste([
   195→            $llave_foranea => $valor_llave_foranea
   196→        ], [
   197→            'empresa_id'      => $empresa_id,
   198→            'empresa_plan_id' => $empresa_plan_id
   199→        ]);
   200→    }
   201→
   202→    /**
   203→     * Obtener los datos de los consumos de un plan en una empresa y sus sucursales junto con sus usuarios responsables.
   204→     *
   205→     * @param Request $request Contenido de la petición HTTP.
   206→     * @return Collection
   207→     * @author Fahibram Cárcamo
   208→     */
   209→    public function obtenerConsumosUsuariosEmpresaSucursales(Request $request): Collection {
   210→        $empresa = $request->filled('empresa_id')
   211→            ? $this->empresa_repositorio->getUno($request->empresa_id)
   212→            : $this->empresa_usuario_repositorio->obtenerEmpresaActualUsuario();
   213→
   214→        $sucursales = $this->empresa_repositorio->obtenerSucursales($empresa->id, new Request());
   215→        $sucursales->push($empresa);
   216→        $sucursales_ids = $sucursales->pluck('id')->toArray();
   217→
   218→        $request->merge(compact('sucursales_ids'));
   219→
   220→        return $this->repositorio->obtenerConsumosUsuariosEmpresaSucursales($request);
   221→    }
   222→}
