     3→namespace App\Http\Services\IA;
     4→
     5→use Carbon\Carbon;
     6→use App\Models\Planes\Plan;
     7→use Illuminate\Support\Str;
     8→use Illuminate\Http\Request;
     9→use App\Models\IA\ConsumoCredito;
    10→use Illuminate\Support\Facades\DB;
    11→use App\Http\Services\BaseService;
    12→use Illuminate\Support\Collection;
    13→use App\Http\Services\Empresas\EmpresaPlanService;
    14→use App\Http\Repositories\IA\ConsumoCreditoRepository;
    15→use App\Http\Repositories\Empresas\EmpresaPlanRepository;
    16→use App\Http\Repositories\Empresas\EmpresaUsuarioRepository;
    17→
    18→class ConsumoCreditoService extends BaseService
    19→{
    20→    /**
    21→     * Constructor.
    22→     *
    23→     * @param ConsumoCreditoRepository $repositorio Repositorio de consumos de créditos.
    24→     * @param ServicioPrecioTokenService $precio_token_servicio Servicio de precios de tokens.
    25→     * @param EmpresaPlanRepository $empresa_plan_repositorio Repositorio de planes de empresa.
    26→     * @param EmpresaPlanService $empresa_plan_servicio Servicio de planes de empresa.
    27→     * @param EmpresaUsuarioRepository $empresa_usuario_repositorio Repositorio de empresas de usuarios.
    28→     * @return void
    29→     * @author Sergio Vargas
    30→     * @author Daniel Beltrán
    31→     * @author Andres Gonzalez
    32→     */
    33→    public function __construct(
    34→        ConsumoCreditoRepository $repositorio,
    35→        private readonly ServicioPrecioTokenService $precio_token_servicio,
    36→        private readonly EmpresaPlanRepository $empresa_plan_repositorio,
    37→        private readonly EmpresaPlanService $empresa_plan_servicio,
    38→        private readonly EmpresaUsuarioRepository $empresa_usuario_repositorio
    39→    ) {
    40→        parent::__construct($repositorio);
    41→    }
    42→
    43→    /**
    44→     * Registra el consumo de tokens desde la respuesta de la API de IA.
    45→     *
    46→     * Maneja tanto el formato nuevo (usage_whisper, usage_gpt) como el formato legacy (usage).
    47→     *
    48→     * @param string $ruta_endpoint Ruta del endpoint llamado (ej: 'generar-vacante').
    49→     * @param array $respuesta_ia Respuesta completa de la API de IA.
    50→     * @param ?int $usuario_id ID del usuario que realizó la petición.
    51→     * @param ?int $empresa_id ID de la empresa del usuario.
    52→     * @param array $contexto Array con agenda_id, vacante_id, etc.
    53→     * @param ?string $payload_peticion JSON del payload enviado.
    54→     * @param ?int $latencia_ms Latencia de la petición en milisegundos.
    55→     * @return ?ConsumoCredito
    56→     * @author Sergio Vargas
    57→     * @author Daniel Beltrán
    58→     * @author Andres Gonzalez
    59→     */
    60→    public function registrarConsumoDesdeRespuesta(
    61→        string $ruta_endpoint,
    62→        array $respuesta_ia,
    63→        ?int $usuario_id = null,
    64→        ?int $empresa_id = null,
    65→        ?array $contexto = [],
    66→        ?string $payload_peticion = null,
    67→        ?int $latencia_ms = null
    68→    ): ?ConsumoCredito {
    69→        // Detectar si hay múltiples metadata de uso (formato nuevo)
    70→        $uso_whisper     = $respuesta_ia['usage_whisper'] ?? null;
    71→        $uso_gpt         = $respuesta_ia['usage_gpt'] ?? null;
    72→        $ultimo_registro = null;
    73→
    74→        // Registrar Whisper si existe
    75→        if ($uso_whisper) {
    76→            $ultimo_registro = $this->registrarConsumoIndividual(
    77→                ruta_endpoint: $ruta_endpoint . '.whisper',
    78→                usage: $uso_whisper,
    79→                respuesta_ia: $respuesta_ia,
    80→                usuario_id: $usuario_id,
    81→                empresa_id: $empresa_id,
    82→                contexto: $contexto,
    83→                payload_peticion: $payload_peticion,
    84→                latencia_ms: $latencia_ms
    85→            );
    86→        }
    87→
    88→        // Registrar GPT si existe
    89→        if ($uso_gpt) {
    90→            $ultimo_registro = $this->registrarConsumoIndividual(
    91→                ruta_endpoint: $ruta_endpoint . '.gpt',
    92→                usage: $uso_gpt,
    93→                respuesta_ia: $respuesta_ia,
    94→                usuario_id: $usuario_id,
    95→                empresa_id: $empresa_id,
    96→                contexto: $contexto,
    97→                payload_peticion: $payload_peticion,
    98→                latencia_ms: $latencia_ms
    99→            );
   100→        }
   101→
   102→        // Fallback: Si solo hay "usage" (formato legacy)
   103→        $usage_legacy = $respuesta_ia['usage'] ?? null;
   104→
   105→        if ($usage_legacy && !$uso_whisper && !$uso_gpt) {
   106→            $ultimo_registro = $this->registrarConsumoIndividual(
   107→                ruta_endpoint: $ruta_endpoint,
   108→                usage: $usage_legacy,
   109→                respuesta_ia: $respuesta_ia,
   110→                usuario_id: $usuario_id,
   111→                empresa_id: $empresa_id,
   112→                contexto: $contexto,
   113→                payload_peticion: $payload_peticion,
   114→                latencia_ms: $latencia_ms
   115→            );
   116→        }
   117→
   118→        // Validar y actualizar estado del plan según créditos restantes
   119→        $this->actualizarEstadoPlanSegunCreditos($empresa_id ?? ($contexto['empresa_id'] ?? null));
   120→
   121→        return $ultimo_registro;
   122→    }
   123→
   124→    /**
   125→     * Actualiza el estado del plan de créditos según el porcentaje de créditos restantes.
   126→     *
   127→     * @return void
   128→     * @author Andres Gonzalez
   129→     * @author Sergio Vargas
   130→     */
   131→    private function actualizarEstadoPlanSegunCreditos(?int $empresa_id = null): void {
   132→        $empresa = $this->empresa_usuario_repositorio->obtenerEmpresaActualUsuario();
   133→        $empresa_id = $empresa_id ?? ($empresa->id ?? null);
   134→
   135→        if (!$empresa_id) {
   136→            return;
   137→        }
   138→
   139→        $plan_creditos = $this->empresa_plan_servicio->creditosDisponiblesEmpresa($empresa_id, new Request());
   140→
   141→        if (!$plan_creditos) {
   142→            return;
   143→        }
   144→
   145→        $empresa_plan_id = $plan_creditos['empresa_plan_id'] ?? null;
   146→        $creditos_plan = $plan_creditos['creditos_plan'] ?? 0;
   147→        $creditos_disponibles = $plan_creditos['creditos_disponibles'] ?? 0;
   148→
   149→        if (!$empresa_plan_id || $creditos_plan <= 0) {
   150→            return;
   151→        }
   152→
   153→        $porcentaje_restante = ($creditos_disponibles / $creditos_plan) * 100;
   154→
   155→        if ($creditos_disponibles <= 0) {
   156→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   157→        } elseif ($porcentaje_restante < 20) {
   158→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_POR_VENCER_ID]);
   159→        }
   160→    }
   161→
   162→    /**
   163→     * Registra un consumo individual de tokens desde una metadata de uso específica.
   164→     *
   165→     * @param string $ruta_endpoint Ruta del endpoint llamado.
   166→     * @param array $usage Metadata de uso específica.
   167→     * @param array $respuesta_ia Respuesta completa de la API de IA.
   168→     * @param ?int $usuario_id ID del usuario que realizó la petición.
   169→     * @param ?int $empresa_id ID de la empresa del usuario.
   170→     * @param array $contexto Array con agenda_id, vacante_id, etc.
   171→     * @param ?string $payload_peticion JSON del payload enviado.
   172→     * @param ?int $latencia_ms Latencia de la petición en milisegundos.
   173→     * @return ?ConsumoCredito
   174→     * @author Sergio Vargas
   175→     * @author Daniel Beltrán
   176→     */
   177→    private function registrarConsumoIndividual(
   178→        string $ruta_endpoint,
   179→        array $usage,
   180→        array $respuesta_ia,
   181→        ?int $usuario_id,
   182→        ?int $empresa_id,
   183→        array $contexto,
   184→        ?string $payload_peticion,
   185→        ?int $latencia_ms,
   186→        ?int $consumo_padre_id = null,
   187→        bool $forzar_tokens_cero = false
   188→    ): ?ConsumoCredito {
   189→        $vacante_id = $contexto['vacante_id'] ?? null;
   190→
   191→        if (!$vacante_id && $payload_peticion) {
   192→            $decoded = json_decode($payload_peticion, true);
   193→
   194→            if (is_array($decoded)) {
   195→                $vacante_id = data_get($decoded, 'vacante_id')
   196→                    ?? data_get($decoded, 'vacanteId')
   197→                    ?? data_get($decoded, 'id')
   198→                    ?? data_get($decoded, 'vacante.id');
   199→
   200→                if ($vacante_id && !is_numeric($vacante_id)) {
   201→                    $vacante_id = null;
   202→                } elseif (is_numeric($vacante_id)) {
   203→                    $vacante_id = (int) $vacante_id;
   204→                }
   205→            }
   206→        }
   207→
   208→        // Usar el usage recibido como parámetro (ya sea usage_whisper o usage_gpt)
   209→        $modelo = $usage['model'] ?? 'desconocido';
   210→
   211→        // Preparar metadata adicional para cálculo de costo (necesario para Whisper)
   212→        $metadata_adicional = [];
   213→
   214→        if (isset($usage['audio_duration_minutes'])) {
   215→            $metadata_adicional['audio_duration_minutes'] = $usage['audio_duration_minutes'];
   216→        }
   217→
   218→        if (isset($usage['audio_duration_seconds'])) {
   219→            $metadata_adicional['audio_duration_seconds'] = $usage['audio_duration_seconds'];
   220→        }
   221→
   222→        // Calcular costo estimado (pasando metadata adicional para Whisper)
   223→        $costo_estimado = $this->precio_token_servicio->calcularCosto(
   224→            $modelo,
   225→            $usage['prompt_tokens'] ?? 0,
   226→            $usage['completion_tokens'] ?? 0,
   227→            $metadata_adicional ?: null
   228→        );
   229→
   230→        // Generar huella digital
   231→        $huella_peticion = null;
   232→
   233→        if ($payload_peticion) {
   234→            $huella_peticion = hash('sha256', $payload_peticion);
   235→        }
   236→
   237→        // Determinar estado de la respuesta
   238→        // Si tiene usage, asumimos que fue exitoso (FastAPI ya lanzaría excepción si hubo error)
   239→        $estado = 'exitoso';
   240→
   241→        if (isset($respuesta_ia['status_code'])) {
   242→            $estado = $respuesta_ia['status_code'] === 200 ? 'exitoso' : 'error';
   243→        } elseif (isset($respuesta_ia['error'])) {
   244→            $estado = 'error';
   245→        }
   246→
   247→        // Preparar datos
   248→        // Determinar extracto de la respuesta (soportar distintos nombres usados por FastAPI)
   249→        $contenido_respuesta = $respuesta_ia['data']
   250→            ?? $respuesta_ia['vacante']
   251→            ?? $respuesta_ia['preguntas']
   252→            ?? $respuesta_ia['pruebas']
   253→            ?? $respuesta_ia['calificacion']
   254→            ?? null;
   255→
   256→        // Si no se encontrИ un payload claro, usar la respuesta completa sin la secciИn usage
   257→        if ($contenido_respuesta === null) {
   258→            $contenido_respuesta = collect($respuesta_ia)->except('usage')->all();
   259→        }
   260→
   261→        $datos = [
   262→            'usuario_id'         => $usuario_id,
   263→            'empresa_id'         => $empresa_id,
   264→            'empresa_plan_id'    => $contexto['empresa_plan_id'] ?? null,
   265→            'consumo_padre_id'   => $consumo_padre_id,
   266→            'servicio_ia_id'     => $contexto['servicio_ia_id'] ?? null,
   267→            'creditos'           => $contexto['creditos'] ?? 0,
   268→            'ruta_endpoint'      => $ruta_endpoint,
   269→            'nombre_servicio'    => $usage['service_name'] ?? null,
   270→            'modelo'             => $modelo,
   271→            'tokens_entrada'     => $usage['prompt_tokens'] ?? 0,
   272→            'tokens_salida'      => $usage['completion_tokens'] ?? 0,
   273→            'tokens_totales'     => $usage['total_tokens'] ?? 0,
   274→            'caracteres_entrada' => $usage['input_chars'] ?? null,
   275→            'costo_estimado_usd' => $costo_estimado,
   276→            'huella_peticion'    => $huella_peticion,
   277→            'extracto_peticion'  => $payload_peticion ? Str::limit($payload_peticion, 500) : null,
   278→            'extracto_respuesta' => $contenido_respuesta ? Str::limit(json_encode($contenido_respuesta), 500) : null,
   279→            'estado'             => $estado,
   280→            'latencia_ms'        => $latencia_ms,
   281→            'mensaje_error'      => $respuesta_ia['error'] ?? null,
   282→            'agenda_id'          => $contexto['agenda_id'] ?? null,
   283→            'vacante_id'         => $vacante_id
   284→        ];
   285→
   286→        if ($forzar_tokens_cero) {
   287→            $datos['tokens_entrada'] = 0;
   288→            $datos['tokens_salida'] = 0;
   289→            $datos['tokens_totales'] = 0;
   290→            $datos['caracteres_entrada'] = null;
   291→            $datos['costo_estimado_usd'] = 0;
   292→        }
   293→
   294→        return $this->repositorio->crear($datos);
   295→    }
