   297→    /**
   298→     * Registrar consumos de créditos divididos entre planes.
   299→     *
   300→     * @param string $ruta_endpoint Ruta del endpoint llamado.
   301→     * @param array $respuesta_ia Respuesta completa de la API de IA.
   302→     * @param array $distribucion Distribución de créditos por plan.
   303→     * @param ?int $usuario_id ID del usuario que rea la petición.
   304→     * @param ?int $empresa_id ID de la empresa del usuario.
   305→     * @param array $contexto Array con agenda_id, vacante_id, etc.
   306→     * @param ?string $payload_peticion JSON del payload enviado.
   307→     * @param ?int $latencia_ms Latencia de la petición en milisegundos.
   308→     * @return array
   309→     * @author Sergio Vargas
   310→     */
   311→    public function registrarConsumoDividido(
   312→        string $ruta_endpoint,
   313→        array $respuesta_ia,
   314→        array $distribucion,
   315→        ?int $usuario_id = null,
   316→        ?int $empresa_id = null,
   317→        ?array $contexto = [],
   318→        ?string $payload_peticion = null,
   319→        ?int $latencia_ms = null
   320→    ): array {
   321→        $registros = [];
   322→
   323→        $usos = [];
   324→        $uso_whisper = $respuesta_ia['usage_whisper'] ?? null;
   325→        $uso_gpt = $respuesta_ia['usage_gpt'] ?? null;
   326→        $uso_legacy = $respuesta_ia['usage'] ?? null;
   327→
   328→        if ($uso_whisper) {
   329→            $usos[] = ['tipo' => 'whisper', 'ruta' => $ruta_endpoint . '.whisper', 'usage' => $uso_whisper];
   330→        }
   331→        if ($uso_gpt) {
   332→            $usos[] = ['tipo' => 'gpt', 'ruta' => $ruta_endpoint . '.gpt', 'usage' => $uso_gpt];
   333→        }
   334→        if ($uso_legacy && !$uso_whisper && !$uso_gpt) {
   335→            $usos[] = ['tipo' => 'legacy', 'ruta' => $ruta_endpoint, 'usage' => $uso_legacy];
   336→        }
   337→
   338→        if (empty($usos)) {
   339→            return [];
   340→        }
   341→
   342→        return DB::transaction(function () use (
   343→            $distribucion,
   344→            $usos,
   345→            $respuesta_ia,
   346→            $usuario_id,
   347→            $empresa_id,
   348→            $contexto,
   349→            $payload_peticion,
   350→            $latencia_ms,
   351→            &$registros
   352→        ) {
   353→            $distribucion_ordenada = array_values($distribucion);
   354→            $principal = $distribucion_ordenada[0] ?? null;
   355→
   356→            foreach ($distribucion_ordenada as $item) {
   357→                if (!($item['creditos'] ?? 0)) {
   358→                    continue;
   359→                }
   360→
   361→                if (!empty($item['es_proximo'])) {
   362→                    $this->empresa_plan_repositorio->actualizar(
   363→                        $item['empresa_plan_id'],
   364→                        ['estado_id' => Plan::ESTADO_ACTIVO_ID]
   365→                    );
   366→                }
   367→            }
   368→
   369→            foreach ($usos as $uso) {
   370→                if (!$principal) {
   371→                    continue;
   372→                }
   373→
   374→                $contexto_principal = array_merge($contexto ?? [], [
   375→                    'empresa_plan_id' => $principal['empresa_plan_id'],
   376→                    'creditos'        => $principal['creditos']
   377→                ]);
   378→
   379→                $principal_registro = $this->registrarConsumoIndividual(
   380→                    ruta_endpoint: $uso['ruta'],
   381→                    usage: $uso['usage'],
   382→                    respuesta_ia: $respuesta_ia,
   383→                    usuario_id: $usuario_id,
   384→                    empresa_id: $empresa_id,
   385→                    contexto: $contexto_principal,
   386→                    payload_peticion: $payload_peticion,
   387→                    latencia_ms: $latencia_ms
   388→                );
   389→
   390→                if ($principal_registro) {
   391→                    $registros[] = $principal_registro;
   392→                }
   393→
   394→                $duplicar = $uso['tipo'] !== 'whisper';
   395→
   396→                if ($duplicar) {
   397→                    foreach (array_slice($distribucion_ordenada, 1) as $item) {
   398→                        $contexto_secundario = array_merge($contexto ?? [], [
   399→                            'empresa_plan_id' => $item['empresa_plan_id'],
   400→                            'creditos'        => $item['creditos']
   401→                        ]);
   402→
   403→                        $registro_secundario = $this->registrarConsumoIndividual(
   404→                            ruta_endpoint: $uso['ruta'],
   405→                            usage: $uso['usage'],
   406→                            respuesta_ia: $respuesta_ia,
   407→                            usuario_id: $usuario_id,
   408→                            empresa_id: $empresa_id,
   409→                            contexto: $contexto_secundario,
   410→                            payload_peticion: $payload_peticion,
   411→                            latencia_ms: $latencia_ms,
   412→                            consumo_padre_id: $principal_registro?->id,
   413→                            forzar_tokens_cero: true
   414→                        );
   415→
   416→                        if ($registro_secundario) {
   417→                            $registros[] = $registro_secundario;
   418→                        }
   419→                    }
   420→                }
   421→            }
   422→
   423→            foreach ($distribucion_ordenada as $item) {
   424→                if (!($item['creditos'] ?? 0)) {
   425→                    continue;
   426→                }
   427→
   428→                if (!empty($item['agota_plan'])) {
   429→                    $this->empresa_plan_repositorio->actualizar(
   430→                        $item['empresa_plan_id'],
   431→                        ['estado_id' => Plan::ESTADO_VENCIDO_ID]
   432→                    );
   433→                }
   434→            }
   435→
   436→            return $registros;
   437→        });
   438→    }
