   440→    /**
   441→     * Obtener historial de consumos de creditos por empresa.
   442→     *
   443→     * @param int $empresa_id ID de la empresa.
   444→     * @param array $filtros Filtros de búsqueda (desde, hasta, servicio_ia_id, usuario_id).
   445→     * @return Collection
   446→     * @author Sergio Vargas
   447→     * @author Jair Carvajal
   448→     * @author Esperanza Olivo
   449→     */
   450→    public function historialConsumosEmpresa(int $empresa_id, array $filtros = []): Collection {
   451→        $desde          = null;
   452→        $hasta          = null;
   453→        $servicio_ia_id = !empty($filtros['servicio_ia_id']) ? (int) $filtros['servicio_ia_id'] : null;
   454→        $usuario_id     = !empty($filtros['usuario_id']) ? (int) $filtros['usuario_id'] : null;
   455→        $consumo_ids   = !empty($filtros['ids_consumos']) ? $filtros['ids_consumos'] : null;
   456→
   457→        if (!empty($filtros['desde'])) {
   458→            $desde = Carbon::parse($filtros['desde'])->startOfDay();
   459→        }
   460→
   461→        if (!empty($filtros['hasta'])) {
   462→            $hasta = Carbon::parse($filtros['hasta'])->endOfDay();
   463→        }
   464→
   465→        $consumos = $this->repositorio->historialPorEmpresa($empresa_id, $desde, $hasta, $servicio_ia_id, $usuario_id, $consumo_ids);
   466→
   467→        return $consumos->map(function (ConsumoCredito $consumo) {
   468→            $servicio = $consumo->servicioIA?->nombre
   469→                ?? $consumo->nombre_servicio
   470→                ?? $consumo->ruta_endpoint;
   471→
   472→            return [
   473→                'usuario'        => $consumo->usuario ? [
   474→                    'id'              => $consumo->usuario->id,
   475→                    'nombre_completo' => $consumo->usuario->nombre_completo
   476→                ] : null,
   477→                'servicio'       => $servicio,
   478→                'creditos'       => $consumo->creditos ?? 0,
   479→                'tokens_reales'  => $consumo->tokens_totales ?? 0,
   480→                'fecha_consumo'  => $consumo->created_at
   481→            ];
   482→        });
   483→    }
   484→}
