 96    /**
 97     * Guardar oferta laboral.
 98     *
 99     * @param Request $request Contenido de la peticin.
100     * @throws Exception Cuando la etapa de pruebas psicomtricas no tiene pruebas configuradas (no se crear el proceso sin esa configuracin).
101     * @return OfertaLaboral
102     * @author Daniel Beltrn
103     * @author Fahibram Crcamo
104     * @author Andres Gonzalez
105     */
106    public function guardar(Request $request): OfertaLaboral {
107        $campos_excluidos = ['_token', '_method', 'id', 'informacion', 'requisitos', 'usuarios_ids', 'horarios', 'idiomas', 'estudios', 'preguntas', 'etapas', 'introduccion_vacante', 'herramientas_tecnologicas', 'comisiones', 'bonificaciones', 'otra_jornada', 'otro_tipo_vehiculo', 'otra_categoria', 'otro_motivo_vacante', 'imagen_para_redes'];
108
109        if ($request->filled('id')) {
110            $oferta_laboral = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
111
112            // Actualizar las etapas que se reciban
113            if ($request->filled('etapas')) {
114                foreach ($request->etapas as $etapa) {
115                    $this->etapa_servicio->guardar(new Request($etapa));
116                }
117            }
118        } else {
119            $usuario = Auth::user();
120            $request->merge([
121                'empresa_id' => $usuario->empresa->id,
122                'usuario_id' => $usuario->id
123            ]);
124
125            // Guardar otra jornada
126            if ($request->filled('otra_jornada')) {
127                $jornada = $this->jornada_repositorio->crear([
128                    'nombre' => $request->otra_jornada,
129                    'otro'   => true
130                ]);
131
132                $request->merge(['jornada_id' => $jornada->id]);
133            }
134
135            $oferta_laboral = $this->repositorio->crear($request->except($campos_excluidos));
136
137            // Asociar la jornada creada a la oferta laboral
138            if (isset($jornada)) {
139                $this->jornada_repositorio->actualizar($jornada->id, ['oferta_laboral_id' => $oferta_laboral->id]);
140            }
141
142            // Crear etapas por defecto
143            $this->etapa_servicio->crearParaOfertaLaboral($oferta_laboral->id);
144
145            // Registrar el consumo de la oferta laboral en el plan de la empresa
146            $this->consumo_servicio->registrarConsumoOfertaLaboral($oferta_laboral);
147        }
148
149        if ($oferta_laboral->estado_id == EstadoOfertaLaboral::PUBLICADA_ID) {
150            $proceso = $this->proceso_repositorio->getUnoAvanzado(new Request(['oferta_laboral_id' => $oferta_laboral->id]));

150            $proceso = $this->proceso_repositorio->getUnoAvanzado(new Request(['oferta_laboral_id' => $oferta_laboral->id]));
151
152            if (is_null($proceso)) {
153                // Verificar si existe la opcin de pruebas psicomtricas en alguna etapa activa de la oferta laboral
154                $etapa_movimiento = $this->etapa_movimiento_repositorio->getUnoAvanzado(new Request([
155                    'oferta_laboral_id'       => $oferta_laboral->id,
156                    'movimiento_id'           => Movimiento::PRUEBAS_PSICOMETRICAS_ID,
157                    'incluir_configuraciones' => true,
158                    'etapa_activa'            => true
159                ]));
160
161                if (!is_null($etapa_movimiento)) {
162                    $configuracion_camara = $this->etapa_movimiento_configuracion_servicio->getRepositorio()->getUnoAvanzado(new Request([
163                        'etapa_movimiento_id'         => $etapa_movimiento->id,
164                        'movimiento_configuracion_id' => 36
165                    ]));
166                    $configuracion_video = $this->etapa_movimiento_configuracion_servicio->getRepositorio()->getUnoAvanzado(new Request([
167                        'etapa_movimiento_id'         => $etapa_movimiento->id,
168                        'movimiento_configuracion_id' => 37
169                    ]));
170                    $pruebas = [];
171
172                    // Crear proceso
173                    $proceso = $this->proceso_repositorio->crear([
174                        'oferta_laboral_id'        => $oferta_laboral->id,
175                        'empresa_id'               => $oferta_laboral->empresa_id,
176                        'usuario_id'               => Auth::id(),
177                        'estado_id'                => EstadoProceso::PENDIENTE_ID,
178                        'nombre'                   => $oferta_laboral->nombre_vacante,
179                        'fecha_inicio'             => now(),
180                        'fecha_cierre'             => $oferta_laboral->fecha_cierre,
181                        'cantidad_vacantes'        => $oferta_laboral->cantidad_vacantes,
182                        'capturar_camara'          => $configuracion_camara->valor,
183                        'capturar_video'           => $configuracion_video->valor,
184                        'dias_vencimiento_agendas' => 5
185                    ]);
186
187                    foreach ($etapa_movimiento->configuraciones as $configuracion) {
188                        if (in_array($configuracion->movimiento_configuracion_id, [28, 29, 30, 31, 32, 33, 34, 35])) {
189                            array_push($pruebas, [
190                                'prueba_id'    => $configuracion->prueba_id,
191                                'perfil_id'    => $configuracion->perfil_id,
192                                'porcentaje'   => $configuracion->valor,
193                                'fecha_limite' => now()->addDays(5)
194                            ]);
195                        }
196                    }
197
198                    if (count($pruebas) === 0) {
199                        throw new Exception('Configure las pruebas psicomtricas para poder continuar.');
200                    }
201
202                    $this->proceso_repositorio->sincronizarPruebas($proceso, $pruebas);
203                }
204            }
205        }
206
207        // Guardar otra jornada
208        if ($request->filled('otra_jornada') && !isset($jornada)) {
209            $jornada = $this->jornada_repositorio->crearOActualizar([
210                'oferta_laboral_id' => $oferta_laboral->id,
211            ], [
212                'nombre' => $request->otra_jornada,
213                'otro'   => true
214            ]);
215
216            $this->repositorio->actualizar($oferta_laboral->id, ['jornada_id' => $jornada->id]);
217            unset($jornada);
218        }
219
220        // Guardar otra categora
221        if ($request->filled('otra_categoria')) {
222            $categoria = $this->categoria_oferta_laboral_repositorio->crearOActualizar([
223                'oferta_laboral_id' => $oferta_laboral->id,
224            ], [
225                'nombre' => $request->otra_categoria,
226                'otro'   => true
227            ]);
228
229            $this->repositorio->actualizar($oferta_laboral->id, ['categoria_id' => $categoria->id]);
230            unset($categoria);
231        }
232
233        // Guardar otro motivo de la vacante
234        if ($request->filled('otro_motivo_vacante')) {
235            $motivo_vacante = $this->motivo_vacante_repositorio->crearOActualizar([
236                'oferta_laboral_id' => $oferta_laboral->id,
237            ], [
238                'nombre' => $request->otro_motivo_vacante,
239                'otro'   => true
