    94→    /**
    95→     * Guardar un plan de selección.
    96→     *
    97→     * @param Request $request Contenido de la petición.
    98→     * @return EmpresaPlan
    99→     * @author Andres Gonzalez
   100→     */
   101→    protected function guardarPlanSeleccion(Request $request): EmpresaPlan {
   102→        $enviar_correo    = false;
   103→        $campos_excluidos = ['_token', '_method', 'id', 'tipo_prueba_id', 'factura', 'vigencia', 'pruebas', 'funcionalidades', 'precio_creditos', 'porcentaje_descuento_creditos', 'plan_credito_id', 'creditos'];
   104→        $plan             = $this->plan_repositorio->getUno($request->plan_id);
   105→        $plan_activo      = $this->repositorio->planActivo($request->empresa_id, $request->tipo_prueba_id);
   106→        $vigencia         = $plan->vigencia;
   107→
   108→        if ($request->filled('vigencia')) {
   109→            $vigencia = $request->vigencia;
   110→        }
   111→
   112→        if (!is_null($plan_activo)) {
   113→            if ($request->filled('id')) {
   114→                // Si el ID recibido es igual al plan activo, no se calcula vigencia
   115→                if ($request->id === $plan_activo->id) {
   116→                    goto omitir_calcular_vigencia_seleccion;
   117→                }
   118→
   119→                // Si la vigencia no ha sido modificada respecto al plan base, no se calcula vigencia
   120→                if ($vigencia == $request->vigencia) {
   121→                    array_push($campos_excluidos, 'fecha_inicio', 'fecha_vencimiento');
   122→                    goto omitir_calcular_vigencia_seleccion;
   123→                }
   124→            }
   125→
   126→            // Poner el nuevo plan como proximo
   127→            $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   128→
   129→            // Validar si el plan activo tiene fecha de vencimiento y el plan seleccionado tiene vigencia
   130→            if (!is_null($plan_activo->fecha_vencimiento) && !is_null($plan->vigencia)) {
   131→                // Agregar las nuevas fechas al nuevo plan
   132→                $hoy          = now();
   133→                $fecha_inicio = ($hoy > $plan_activo->fecha_vencimiento) ? $hoy->copy() : Carbon::createFromDate($plan_activo->fecha_vencimiento)->addDay();
   134→                $request->merge($this->calcularFechasVigencia($vigencia, $fecha_inicio));
   135→            }
   136→        } else {
   137→            $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   138→
   139→            // Validar si el plan seleccionado tiene vigencia
   140→            if (!is_null($plan->vigencia)) {
   141→                $request->merge($this->calcularFechasVigencia($vigencia));
   142→            }
   143→        }
   144→
   145→        omitir_calcular_vigencia_seleccion:
   146→
   147→        if (!$request->filled('unidades')) {
   148→            $request->merge(['unidades' => $plan->unidades]);
   149→        } elseif ($request->unidades != $plan->unidades) {
   150→            $request->merge(['plan_original' => false]);
   151→        }
   152→
   153→        if (!$request->filled('cantidad_ofertas_laborales')) {
   154→            $request->merge(['cantidad_ofertas_laborales' => $plan->cantidad_ofertas_laborales]);
   155→        } elseif ($request->cantidad_ofertas_laborales != $plan->cantidad_ofertas_laborales) {
   156→            $request->merge(['plan_original' => false]);
   157→        }
   158→
   159→        if (!$request->filled('cantidad_usuarios')) {
   160→            $request->merge(['cantidad_usuarios' => $plan->cantidad_usuarios]);
   161→        } elseif ($request->cantidad_usuarios != $plan->cantidad_usuarios) {
   162→            $request->merge(['plan_original' => false]);
   163→        }
   164→
   165→        if (!$request->filled('cantidad_pruebas')) {
   166→            $request->merge(['cantidad_pruebas' => $plan->cantidad_pruebas]);
   167→        } elseif ($request->cantidad_pruebas != $plan->cantidad_pruebas) {
   168→            $request->merge(['plan_original' => false]);
   169→        }
   170→
   171→        // Comprobar si se modificaron las funcionalidades respecto al plan original
   172→        if (!is_null($plan->funcionalidades) && $request->filled('funcionalidades')) {
   173→            $funcionalidades_originales = $plan->funcionalidades->pluck('id')->toArray();
   174→            $primera_comparacion        = array_diff($request->funcionalidades, $funcionalidades_originales);
   175→            $segunda_comparacion        = array_diff($funcionalidades_originales, $request->funcionalidades);
   176→
   177→            if (count([...$primera_comparacion, ...$segunda_comparacion]) > 0) {
   178→                $request->merge(['plan_original' => false]);
   179→            }
   180→        }
   181→
   182→        if ($request->filled('id')) {
   183→            // Eliminar la factura actual
   184→            if ($request->hasFile('factura')) {
   185→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   186→
   187→                if (!is_null($archivo)) {
   188→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   189→                    $this->archivo_repositorio->eliminar($archivo->id);
   190→                }
   191→            }
   192→
   193→            $plan_asignado = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
   194→        } else {
   195→            $enviar_correo = true;
   196→            $request->merge(['usuario_id' => auth()->user()->id]);
   197→            $plan_asignado = $this->repositorio->crear($request->except($campos_excluidos));
   198→        }
   199→
   200→        if ($request->filled('pruebas')) {
   201→            $this->repositorio->sincronizarPruebas($plan_asignado, $request->pruebas);
   202→        }
   203→
   204→        if ($request->filled('funcionalidades')) {
   205→            $this->repositorio->sincronizarFuncionalidades($plan_asignado, $request->funcionalidades);
   206→        }
   207→
   208→        if ($request->hasFile('factura')) {
   209→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   210→        }
   211→
   212→        if ($enviar_correo) {
   213→            $empresa      = $this->empresa_repositorio->getUno($request->empresa_id);
   214→            $usuario      = $empresa->usuarios[0];
   215→            $empresa_plan = $this->repositorio->getUno($plan_asignado->id);
   216→            $titulo       = 'Usuario principal de la empresa creado';
   217→            Mail::send(new CrearUsuarioMail($usuario, Password::createToken($usuario), $titulo, $titulo, '', $empresa, $empresa_plan));
   218→        }
   219→
   220→        return $plan_asignado;
   221→    }
