   230→    protected function guardarPlanCreditos(Request $request): EmpresaPlan {
   231→        $enviar_correo = false;
   232→        $plan_credito  = $this->plan_credito_repositorio->getUno($request->plan_credito_id);
   233→        $plan_activo   = $this->repositorio->planCreditosActivo($request->empresa_id);
   234→
   235→        // Preparar datos para el plan de créditos
   236→        $datos_creditos = [
