   238→            'plan_id'               => null,
   239→            'plan_credito_id'       => $request->plan_credito_id,
   240→            'creditos'              => $plan_credito->creditos ?? 0,
   241→            'precio'                => $request->precio_creditos ?? $plan_credito->precio ?? null,
   242→            'porcentaje_descuento'  => $request->porcentaje_descuento_creditos ?? null,
   243→            'numero_factura'        => $request->numero_factura,
   244→            'comentario'            => $request->comentario,
   245→            'fecha_inicio'          => null,
   246→            'fecha_vencimiento'     => null,
   247→            'usuario_id'            => auth()->user()->id,
   248→        ];
   249→
   250→        // Si es edición de un plan de créditos existente
   251→        if ($request->filled('id')) {
   252→            // Eliminar la factura actual si se sube una nueva
   253→            if ($request->hasFile('factura')) {
   254→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   255→
   256→                if (!is_null($archivo)) {
   257→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   258→                    $this->archivo_repositorio->eliminar($archivo->id);
   259→                }
   260→            }
   261→
   262→            $plan_asignado = $this->repositorio->actualizar($request->id, $datos_creditos);
   263→        } else {
   264→            // Determinar estado para nuevo plan
   265→            if (!is_null($plan_activo) && $plan_activo->id != $request->id) {
   266→                $datos_creditos['estado_id'] = Plan::ESTADO_PROXIMO_ID;
   267→            } else {
   268→                $datos_creditos['estado_id'] = Plan::ESTADO_ACTIVO_ID;
   269→            }
   270→
   271→            $enviar_correo = true;
   272→            $plan_asignado = $this->repositorio->crear($datos_creditos);
   273→        }
   274→
   275→        if ($request->hasFile('factura')) {
   276→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   277→        }
   278→
   279→        return $plan_asignado;
