   282→    /**
   283→     * Renovar el plan asignado.
   284→     *
   285→     * @param int $id ID del plan de la empresa que se quiere renovar.
   286→     * @throws Exception Si el plan que se quiere renovar está en estado 'próximo'.
   287→     * @return EmpresaPlan Plan de la empresa renovado.
   288→     * @author Daniel Beltrán
   289→     */
   290→    public function renovar(int $id): EmpresaPlan {
   291→        $empresa_plan = $this->repositorio->getUno($id);
   292→
   293→        if ($empresa_plan->estado_id === Plan::ESTADO_PROXIMO_ID) {
   294→            throw new Exception('No se puede renovar un plan que no ha entrado en vigencia');
   295→        }
   296→
   297→        $request = new Request(Arr::except($empresa_plan->toArray(), ['id', 'renovando', 'por_facturar', 'plan', 'estado', 'vigencia_dias']));
   298→
   299→        $request->merge(['por_facturar' => true]);
   300→
   301→        if (in_array($empresa_plan->estado_id, [Plan::ESTADO_ACTIVO_ID, Plan::ESTADO_POR_VENCER_ID])) {
   302→            // Cuando el plan actual está activo o por vencer, se asigna el estado 'próximo'
   303→            $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   304→        } elseif (in_array($empresa_plan->estado_id, [Plan::ESTADO_INACTIVO_ID, Plan::ESTADO_VENCIDO_ID])) {
   305→            // Cuando el plan actual está inactivo o vencido, se asigna el estado 'activo'
   306→            $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   307→        }
   308→
   309→        $this->verificarYAsignarTiempo($empresa_plan, $request);
   310→
   311→        $plan_renovado = $this->repositorio->crear($request->toArray());
   312→
   313→        if ($request->filled('pruebas')) {
   314→            $this->repositorio->sincronizarPruebas($plan_renovado, $request->pruebas);
   315→        }
   316→
   317→        if ($request->filled('funcionalidades')) {
   318→            $this->repositorio->sincronizarFuncionalidades($plan_renovado, $request->funcionalidades);
   319→        }
   320→
   321→        return $plan_renovado;
   322→    }
