   324→    /**
   325→     * Verificar si el plan es por tiempo y asignar el tiempo de vigencia en caso de serlo.
   326→     *
   327→     * @param EmpresaPlan $empresa_plan Plan de la empresa.
   328→     * @param Request $request Contenido de la petición.
   329→     * @return void
   330→     * @author Daniel Beltrán
   331→     */
   332→    public function verificarYAsignarTiempo(EmpresaPlan $empresa_plan, Request &$request): void {
   333→        if (!is_null($empresa_plan->fecha_inicio) && !is_null($empresa_plan->fecha_vencimiento)) {
   334→            $request->merge($this->calcularFechasVigencia($empresa_plan->plan->vigencia, $empresa_plan->fecha_vencimiento->addDay()));
   335→        }
   336→    }
   337→
   338→    /**
   339→     * Calcular las fechas en las que el plan estará vigente.
   340→     *
   341→     * @param int $vigencia Cantidad de días de vigencia.
   342→     * @param Carbon|null $fecha_inicio_vigencia Fecha en la que inicia la vigencia.
   343→     * @return array Fechas de inicio y vencimiento.
   344→     * @author Daniel Beltrán
   345→     */
   346→    public function calcularFechasVigencia(int $vigencia, ?Carbon $fecha_inicio_vigencia = null): array {
   347→        $hoy = now();
   348→
   349→        $fecha_inicio = !is_null($fecha_inicio_vigencia) ? $fecha_inicio_vigencia : $hoy;
   350→
   351→        // Si es múltiplo de 3, entonces la vigencia es en meses
   352→
   353→        if (($vigencia % 3) === 0 && gettype($vigencia / 30) === 'integer') {
   354→            $meses = $vigencia / 30;
   355→
   356→            $fecha_vencimiento = $fecha_inicio->copy()->addMonths($meses);
   357→
   358→            if ($fecha_inicio->day === 1) {
   359→                $fecha_vencimiento->subDay();
   360→            }
   361→        } else {
   362→            $vigencia = $vigencia < 0 ? abs($vigencia) : $vigencia; // Convierte los valores negativos a positivos
