   166→    /**
   167→     * Obtener todas las empresas.
   168→     *
   169→     * Obtiene todas las empresas y les agrega información de los últimos planes que han tenido por cada tipo de prueba excluyendo los planes en estado próximo.
   170→     *
   171→     * @param Request $request Contenido de la petición.
   172→     * @return Collection
   173→     * @author Daniel Beltrán
   174→     * @author Andres Gonzalez
   175→     */
   176→    public function getTodo(Request $request): Collection {
   177→        $empresas      = $this->repositorio->getTodo($request);
   178→        $tipos_pruebas = $this->tipo_prueba_repositorio->getTodo(new Request());
   179→        $coleccion     = [];
   180→
   181→        foreach ($empresas as $empresa) {
   182→            $registro = $empresa;
   183→
   184→            foreach ($tipos_pruebas as $tipo_prueba) {
   185→                $llave = 'plan_' . Str::slug($tipo_prueba->nombre, '_');
   186→
   187→                // Obtener el plan activo, si no tiene se obtiene el último plan asignado
   188→                $registro[$llave] = $this->empresa_plan_repositorio->planActivo($empresa->id, $tipo_prueba->id);
   189→
   190→                if (is_null($registro[$llave])) {
   191→                    $registro[$llave] = $this->empresa_plan_repositorio->ultimoPlanAsignado(new Request([
   192→                        'empresa_id'             => $empresa->id,
   193→                        'tipo_prueba_id'         => $tipo_prueba->id,
   194→                        'excluir_estado_proximo' => true
   195→                    ]));
   196→                }
   197→
   198→                // Verificar si tiene un plan próximo adicional al plan actual
   199→                if (!is_null($registro[$llave])) {
   200→                    $plan_proximo                     = $this->empresa_plan_repositorio->planProximo($empresa->id, $tipo_prueba->id);
   201→                    $registro[$llave]['plan_proximo'] = !is_null($plan_proximo) && $registro[$llave]->id !== $plan_proximo->id;
   202→                }

