 * @author Andres Gonzalez
 * @author Jair Carvajal
 */
public function getUno(int $id): ?Empresa {
    $empresa = $this->repositorio->getUnoBasico($id);

    if (!is_null($empresa)) {
        $planes        = [];
        $tipos_pruebas = $this->tipo_prueba_repositorio->getTodo(new Request());

        foreach ($tipos_pruebas as $tipo_prueba) {
            $plan = $this->empresa_plan_servicio->obtenerPlanActivo($id, $tipo_prueba->id)
                ?? $this->empresa_plan_servicio->obtenerUltimoPorTipo($id, $tipo_prueba->id);

            if (!is_null($plan)) {
                array_push($planes, $plan);
            }
        }

        $usuarios = $this->empresa_usuario_repositorio->getTodo(new Request([
            'empresa_id'         => $id,
            'con_relaciones'     => true,
            'validar_relaciones' => true,
            'en'                 => true,
            'campo'              => 'rol_id',
            'valores'            => [
                $this->rol_repositorio::ROL_SUPERADMIN_ID,
                $this->rol_repositorio::ROL_ADMINISTRADOR_ID,
                $this->rol_repositorio::ROL_CONTROLADOR_ID,
                $this->rol_repositorio::ROL_PROPIETARIO_ID,
                $this->rol_repositorio::ROL_ADMINISTRADOR_GENERAL_ID,
                $this->rol_repositorio::ROL_ADMINISTRADOR_SUCURSAL_ID,
                $this->rol_repositorio::ROL_EVALUADOR_ID
            ]
        ]));

        $usuarios = $usuarios->map(function ($usuario) {
            $eliminar = false;

            if (
                !$usuario->usuario->tiene_agendas
                && !$usuario->usuario->tiene_ofertas_laborales
                && !$usuario->usuario->tiene_procesos
                && !$usuario->usuario->tiene_procesos
                && !$usuario->usuario->tiene_etapa_usuario
            ) {
                $eliminar = true;
            }

            $usuario->usuario->eliminar = $eliminar;
            return $usuario;
        });

        $empresa->planes                         = $planes;
        $empresa->usuarios                       = $usuarios;
        $empresa->configuraciones_defecto        = $this->configuracion_repositorio->getTodo(new Request(['modelo' => $this->modelo]));
        $empresa->configuraciones_personalizadas = $this->configuracion_modelo_repositorio->getTodo(new Request([
            'model_type' => $this->modelo,
            'modelo_id'  => $id
        ]));
        $empresa->creditos_promedio_accion = $this->servicio_ia_repositorio->obtenerPromedioCreditos();
    }

    return $empresa;
}

/**
 * Obtener todas las empresas.
 *
 * Obtiene todas las empresas y les agrega información de los últimos planes que han tenido por cada tipo de prueba excluyendo los planes en estado próximo.
 *
 * @param Request $request Contenido de la petición.
 * @return Collection
 * @author Daniel Beltrán
 * @author Andres Gonzalez
 */
public function getTodo(Request $request): Collection {
    $empresas      = $this->repositorio->getTodo($request);
    $tipos_pruebas = $this->tipo_prueba_repositorio->getTodo(new Request());
    $coleccion     = [];

