function mostrarPlanes() {
    let empresa_id = empresa_seleccionada.id;
    let buscar = $('#buscador-plan').val();
    let filtro_tipo_prueba_id = $('#filtro_tipo_prueba_id').val();
    if ($.fn.DataTable.isDataTable('#tabla-planes')) {
        tabla_planes.destroy();
    }
    $('#buscador-plan').remove();
    $('#tbody-planes').empty();
    let planes = empresa_seleccionada.planes;
    if (planes.length > 0) {
        for (let i = 0; i < planes.length; i++) {
            if (!planes[i].plan || !planes[i].plan.tipo_prueba_id) {
                continue;
            }
            if (
                (filtro_tipo_prueba_id == 'Todos') ||
                (filtro_tipo_prueba_id == '1' && planes[i].plan.tipo_prueba_id == 1) ||
                (filtro_tipo_prueba_id == '2' && planes[i].plan.tipo_prueba_id == 2) ||
                (filtro_tipo_prueba_id == '3' && planes[i].plan.tipo_prueba_id != 3)
            ) {
                let estado = obtenerEstadoPlan(
                    planes[i].estado.id,
                    planes[i].estado.nombre,
                    planes[i].por_facturar,
                    planes[i].renovando,
                    null,
                    planes[i].plan_proximo_id
                );

                let acciones = obtenerAcciones(
                    planes[i].estado.id,
                    planes[i].id, empresa_id,
                    planes[i].plan.tipo_prueba_id,
                    planes[i].plan.nombre,
                    planes[i]?.archivos[0]?.id ?? '0',
                    empresa_seleccionada.nombre_comercial
                );

                let modificado = '';
                if (planes[i].plan_original == 0) {
                    modificado = `<i class="bi bi-wrench me-3 text-blue-40" ${crearTooltip({placement: 'top', content: 'Plan modificado', toggle: 'popover' })}></i>`;
                }
                let fecha_vencimiento = '';
                if (planes[i].fecha_vencimiento != null) {
                    fecha_vencimiento = `<span class="ms-3">Vencimiento: ${obtenerFecha(planes[i].fecha_vencimiento)}</span>`;
                }
                let fecha_creacion = '';
                if (planes[i].created_at != null) {
                    fecha_creacion = `Creación: ${obtenerFecha(planes[i].created_at)}`;
                }
                let fecha_inicio = '';
                if (planes[i].fecha_inicio != null) {
                    fecha_inicio = `Activación: ${obtenerFecha(planes[i].fecha_inicio)}`;
                }
                let pruebas_plan = '';
                if (planes[i].pruebas !== undefined) {
                    let pruebas = planes[i].pruebas;
                    for (let j = 0; j < pruebas.length; j++) {
                        pruebas_plan += `
                            <div
                                class="col-auto me-3 circulo-prueba"
                                style="background-image: url(../imagenes/logos/pruebas/${slugify(planes[i].pruebas[j].nombre)}.png)"
                                ${crearTooltip({placement: 'top', content: planes[i].pruebas[j].nombre, toggle: 'popover' })}
                            ></div>
                        `;
                    }
                }
                let icono = '';
                let title = '';
                if (planes[i].plan.tipo_prueba_id == 1) {
                    title = 'Selección';
                    icono = 'bi-person-fill';
                } else if (planes[i].plan.tipo_prueba_id == 2) {
                    title = 'Organizacional';
                    icono = 'bi-people-fill';
                } else if (planes[i].plan.tipo_prueba_id == 3) {
                    title = 'Vocacional';
                    icono = 'bi-mortarboard-fill';
                }
                let opcion_editar = `
                    <button type="button" class="btn btn-text-primary-border text-white btn-circle no-visited" title="Editar" onclick="window.location.href='/empresas-planes/${planes[i].id}/edit'">
                        <i class="bi bi-pencil-fill"></i>
                    </button>
                `;
                if (planes[i]?.plan_proximo_id) {
                    opcion_editar = `
                        <button type="button" class="btn btn-text-primary-border text-white btn-circle no-visited dropdown" title="Editar">
                            <i class="bi bi-pencil-fill" data-bs-toggle="dropdown" data-bs-auto-close="outside" aria-expanded="false"></i>
