/**
 * Crea el componente HTML de los progress bars de los consumos.
 *
 * @param {string} label Etiqueta del progress bar.
 * @param {string} icono Icono del progress bar.
 * @param {number} valor Valor actual del consumo.
 * @param {number} total Valor total del consumo.
 * @param {boolean} un_text Booleano para mostrar o no el texto.
 * @returns {string} HTML del progress bar.
 * @author Andres Morales
 * @author Andres Gonzalez
 */
function crearProgressBarTemplate(label, icono, valor, total, un_text) {
    let icono_html = '';
    if (icono) {
        if (icono.startsWith('/') || icono.includes('.svg')) {
            icono_html = `<img src="${icono}" alt="${label}" class="me-2">`;
        } else {
            icono_html = `<i class="bi bi-${icono} text-headline-5 me-2"></i>`;
        }
    }
    return `
        ${un_text ? '<p class="text-label-1 mb-1">' : ''}${icono_html}${label}${un_text ? '</p>' : ''}
        ${crearProgressBar({
            label: `${valor}/${total ?? 0}`,
            height: '8px',
            porcentaje: { porcentaje: (valor / (total ?? 1)) * 100, texto: false }
        })}
    `;
}

/**
 * Retornar el componente html de la lista de cantidades de consumos agrupados por sucursal.
 *
 * @param {Object} empresa_plan Registro de la tabla empresas_planes.
 * @param {array} sucursales_consumos_agendas Arreglo de los conteos de los consumos de agendas de las sucursales de la empresa.
