 * @returns {string}
 * @author Andres Morales
 * @author Fahibram Cárcamo
 * @author Sergio Vargas
 */
function listarCantidadConsumosSucursales (empresa_plan, sucursales_consumos_agendas, sucursales_consumos_ofertas_laborales) {
    if (!empresa_plan.plan) {
        return '';
    }
    return sucursales.map(sucursal => {
        let conteos_consumos_agendas_sucursal = sucursales_consumos_agendas.find(sucursal_busqueda => sucursal_busqueda.id === sucursal.id);
        let conteos_consumos_ofertas_laborales_sucursal = sucursales_consumos_ofertas_laborales.find(sucursal_busqueda => sucursal_busqueda.id === sucursal.id);

        let total_agendas_consumidas_sucursal = conteos_consumos_agendas_sucursal?.consumos_agrupados_por_empresa_plan
            ?.find(conteo => conteo.empresa_plan_id === empresa_plan.id)
            ?.conteo_consumos ?? 0;

        let total_ofertas_laborales_consumidas_sucursal = conteos_consumos_ofertas_laborales_sucursal?.consumos_agrupados_por_empresa_plan
            ?.find(conteo => conteo.empresa_plan_id === empresa_plan.id)
            ?.conteo_consumos ?? 0;

        let total_usuarios_sucursal = obtenerTotalUsuarios(sucursal);
        let plan = empresa_plan.plan;

        return `
            <tr class="contenedor_plan_hijo_${empresa_plan.id}_${plan.id}_${empresa_plan.numero_factura} d-none">
                <td class="py-0 my-0">
                    <div class="card border-0">
                        <div class="card-body py-2 px-10">
                            <div class="row">
                                <div class="col-2 my-auto text-body-3 text-wrap">
                                    ${sucursal.nombre_comercial}
                                </div>
                                <div class="col my-auto">
                                    <div class="card bg-white border-0">
                                        <div class="card-body py-2">
                                            <div class="row">
                                                <div class="col-4 px-7">
                                                    ${
                                                        crearProgressBarTemplate(
                                                            'Unidades',
                                                            'box',
                                                            total_agendas_consumidas_sucursal,
                                                            plan.unidades,
                                                            false
                                                        )
                                                    }
                                                </div>
                                                <div class="col-4 px-7">
                                                    ${
                                                        crearProgressBarTemplate(
                                                            'Vacantes',
                                                            'briefcase',
                                                            total_ofertas_laborales_consumidas_sucursal,
                                                            plan.cantidad_ofertas_laborales,
                                                            false
                                                        )
                                                    }
                                                </div>
                                                <div class="col-4 px-7">
                                                    ${
                                                        crearProgressBarTemplate(
                                                            'Usuarios',
                                                            'person',
                                                            total_usuarios_sucursal,
                                                            plan.cantidad_usuarios,
                                                            false
                                                        )
                                                    }
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </td>
            </tr>
        `;
    }).join('');
}

/**
 * Agrupar los consumos de un plan por empresa.
 *
 * @param {Object} empresa_plan_consumos Consumos del registro de la tabla empresas_planes.
 * @returns {array}
 * @author Andres Morales
 */
function obtenerConsumos(empresa_plan_consumos) {
    if (!empresa_plan_consumos || empresa_plan_consumos.length === 0) return sucursales;

    let consumos_empresas = new Map();

    empresa_plan_consumos.forEach(consumo => {
        let empresa = consumo.empresa;
        if (!consumos_empresas.has(empresa.id)) {
            empresa.consumos = [];
            consumos_empresas.set(empresa.id, empresa);
        }
        consumos_empresas.get(empresa.id).consumos.push(consumo);
    });

    sucursales.forEach(sucursal => {
        if (!consumos_empresas.has(sucursal.id)) {
            consumos_empresas.set(sucursal.id, sucursal);
        }
    });

    return Array.from(consumos_empresas.values());
}

/**
 * Retornar la suma total de usuarios de una empresa o de todas las empresas.
 *
 * @param {Object|null} empresa Empresa a la que pertenecen los usuarios.
 * @returns {number}
 * @author Andres Morales
 */
function obtenerTotalUsuarios(empresa = null) {
    let usuarios_temporal = empresa?.usuarios ?? usuarios;
    let usuarios_ids_temporal = new Set();
    usuarios_temporal.forEach(usuario => {
        if (usuarios_ids.includes(usuario.id)) {
            usuarios_ids_temporal.add(usuario.id);
        }
    });
    return usuarios_ids_temporal.size;
}

/**
 * Abrir y cerrar el contenido de la lista de consumos por sucursal.
 *
 * @param {string} id Id del elemento a mostar u ocultar.
 * @param {string} elemento Elemento padre del menú de acciones.
 * @returns {void}
 * @author Andres Morales
 */
function toggleSucursalesList(id, elemento) {
    $('.contenedor_plan_hijo_' + id).toggleClass('d-none');
    $(elemento).find('i').toggleClass('bi-caret-down-fill bi-caret-up-fill');
}

/**
 * Realizar la consulta HTTP para solicitar la renovación de un plan.
 *
 * @returns {void}
 * @author Fahibram Cárcamo
 * @author Andres Morales
 * @author Andres González
 */
async function solicitarRenovacionPlan() {
    $('#modal-solicitar-renovacion').modal('hide');
    let plan_id = $('#id-plan-solicitar-renovacion').val();
    let tipo_plan = $('#tipo-plan-solicitar-renovacion').val();
    await $.ajax({
        type: 'POST',
        url : '/solicitud-renovacion-plan',
        data: {
            plan_id,
            tipo_plan,
            empresa_id,
            usuario_id,
            _token: $('meta[name="csrf-token"]').attr('content')
        }
    }).then(respuesta => {
        $('#nombre-plan-solicitar-renovacion').text('');
        $('#id-plan-solicitar-renovacion').val('');
        $('#tipo-plan-solicitar-renovacion').val('');
        mostrarToast('success', 'Solicitud de renovación de plan realizada');
    }).catch((error) => {
        mostrarToast('danger', 'Ocurrió un error en la solicitud');
    });
}

/**
 * Abrir el modal de confirmación de solicitud de renovación de un plan.
 *
 * @param {number} plan_id Id del plan o plan de créditos.
 * @param {string} nombre_plan Nombre del plan.
 * @param {string} tipo_plan Tipo de plan ('plan' o 'plan_credito').
 * @returns {void}
 * @author Fahibram Cárcamo
 * @author Andres González
 */
function abrirModalSolicitudRenovacionPlan(plan_id, nombre_plan, tipo_plan = 'plan') {
    $('#nombre-plan-solicitar-renovacion').text(nombre_plan);
    $('#id-plan-solicitar-renovacion').val(plan_id);
    $('#tipo-plan-solicitar-renovacion').val(tipo_plan);
    $('#modal-solicitar-renovacion').modal('show');
}

/**
 * Abrir la factura del plan en una nueva pestaña del navegador.
 *
 * @param {number} factura_id Id de la factura.
 * @returns {void}
 * @author Fahibram Cárcamo
 * @author Andres Morales
 */
async function abrirFacturaPlan(factura_id){
    if (factura_id) {
        await $.ajax({
            type: 'GET',
            url: `archivos-abrir/${factura_id}`,
            xhrFields: {responseType: 'blob'},
        })
            .then(respuesta => {
                let ruta_factura = window.URL.createObjectURL(respuesta);
                window.open(ruta_factura, '_blank');
            })
            .catch((error) => {
                mostrarToast('danger', 'Ocurrió un error en la solicitud');
            });
    } else {
        mostrarToast('danger', 'No se encontró la factura');
    }
}

