 * @param string $modelo Modelo.
 * @param int $modelo_id ID del modelo.
 * @param int $configuracion_id ID de la configuración.
 * @return ConfiguracionModelo|Configuracion
 * @author Daniel Beltrán
 */
public function obtenerConfiguracion(string $modelo, int $modelo_id, int $configuracion_id): ConfiguracionModelo|Configuracion {
    $configuracion = $this->repositorio->obtenerUnoPorModeloYConfiguracion($modelo, $modelo_id, $configuracion_id);

    if (is_null($configuracion)) {
        $configuracion = $this->configuracion_repositorio->getUno($configuracion_id);

        if (is_null($configuracion)) {
            $exception = new ModelNotFoundException('No se encontró la configuración solicitada.');
            $exception->setModel(Configuracion::class, [$configuracion_id]);

            throw $exception;

