/**
 * Reagendar participantes.
 *
 * @param ReagendarParticipantesRequest $request Contenido de la petición.
 * @return JsonResponse
 * @author Daniel Beltrán
 * @author Sergio Vargas
 */
public function reagendarParticipantes(ReagendarParticipantesRequest $request): JsonResponse {
    $codigo    = 200;
    $respuesta = [];

    try {
        DB::beginTransaction();
        $resultados = $this->servicio->reagendarParticipantes($request);
        DB::commit();

        $respuesta = $this->servicio->construirRespuestaReagendamientos($resultados);
    } catch (Exception $error) {
        DB::rollBack();

        $codigo    = 400;
        $respuesta = [
            'message' => 'Ocurrió un error',
            'errors'  => $error->getMessage()
        ];
    }

    return new JsonResponse($respuesta, $codigo);
}

