135→/**
136→ * Seleccionar un participante.
137→ *
138→ * @param {number} id ID del participante.
139→ * @returns {void}
140→ * @author Daniel Beltrán
141→ */
142→function seleccionarParticipante(id) {
143→    if (participantes_seleccionados.find((item) => item === id)) {
144→        participantes_seleccionados = participantes_seleccionados.filter((item) => item !== id);
145→    } else {
146→        participantes_seleccionados.push(id);
147→        participantes_seleccionados = participantes_seleccionados.filter((item, index) => participantes_seleccionados.indexOf(item) === index);
148→    }
149→
150→    habilitarBotonesAcciones();
151→}
