153→/**
154→ * Descartar uno o más participantes.
155→ *
156→ * Si no se recibe el ID de algún participante en específico, se descartarán todos los participantes seleccionados.
157→ *
158→ * @param {number|null} id ID del participante.
159→ * @returns {Promise<void>}
160→ * @author Daniel Beltrán
161→ */
162→async function descartarParticipantes(id = null) {
163→    if (id !== null) {
164→        participantes_seleccionados = [id];
165→    }
166→
167→    await $.ajax({
168→        url : `/participantes-descartar`,
169→        type: 'POST',
170→        data: {
171→            _token       : $('input[name="_token"]').val(),
172→            id           : PROCESO.id,
173→            participantes: participantes_seleccionados
174→        }
175→    }).done(function (respuesta) {
176→        participantes_seleccionados = [];
177→        habilitarBotonesAcciones();
178→        mostrarToast('success', respuesta.message);
179→        cargarListadoParticipantes();
180→    }).fail(function () {
181→        console.log('Error');
182→    });
183→}
