198→/**
199→ * Mover participante de proceso.
200→ *
201→ * @returns {Promise<void>}
202→ * @author Daniel Beltrán
203→ */
204→async function moverDeProceso() {
205→    const DATOS = new FormData();
206→    DATOS.append('_token', $('input[name="_token"]').val());
207→    DATOS.append('proceso_actual_id', PROCESO.id);
208→    DATOS.append('proceso_destino_id', $('#proceso_id').val());
209→    DATOS.append('participantes', JSON.stringify(participantes_seleccionados));
210→    DATOS.append('mantener_en_proceso', ($('#mantener_en_proceso').prop('checked') ? 1 : 0));
211→
212→    // Agregar el campo de cantidad de días o fecha límite, según el caso
213→    if ($('#es_fecha').prop('checked') == 1) {
214→        DATOS.append('fecha_vencimiento_agendas', $('#fecha_vencimiento_agendas').val());
215→        DATOS.delete('dias_vencimiento_agendas');
216→    } else if ($('#es_dias').prop('checked') == 1) {
217→        DATOS.append('dias_vencimiento_agendas', $('#dias_vencimiento_agendas').val());
218→        DATOS.delete('fecha_vencimiento_agendas');
219→    }
220→
221→    await $.ajax({
222→        url : `/participantes-cambiar-proceso`,
223→        type: 'POST',
224→        data: DATOS,
225→        processData: false,
226→        contentType: false
227→    }).done(function (respuesta) {
228→        participantes_seleccionados = [];
229→        $('#modal-mover-proceso').modal('hide');
230→        $('#formulario-mover-proceso').trigger('reset');
231→        habilitarBotonesAcciones();
232→        mostrarToast('success', respuesta.message);
233→        cargarListadoParticipantes();
234→    }).fail(function (error) {
235→        mostrarToast('danger', error.responseJSON.errors);
236→    });
237→}
