314→/**
315→ * Reenviar agendamiento.
316→ *
317→ * @returns {Promise<void>}
318→ * @author Daniel Beltrán
319→ * @author Sergio Vargas
320→ */
321→async function reenviarAgendamiento() {
322→    const ID         = $('#participante_id_reagendar').val();
323→    const FORMULARIO = $('#formulario-reagendar');
324→
325→    FORMULARIO.validate({
326→        ignore: [] // No ignorar campos ocultos
327→    });
328→
329→    if (!FORMULARIO.valid()) {
330→        return;
331→    }
332→
333→    const DATOS = new FormData(FORMULARIO[0]);
334→
335→    ['capturar_camara', 'capturar_video', 'vigencia_pruebas', 'pruebas', 'dias_vencimiento_agendas', 'fecha_vencimiento_agendas'].forEach(campo => {
336→        DATOS.delete(campo);
337→    });
338→
339→    DATOS.append('_token', $('input[name="_token"]').val());
340→    DATOS.append('proceso_id', PROCESO.id);
341→
342→    // Agregar el campo de cantidad de días o fecha límite, según el caso
343→    if ($('#es_fecha1').prop('checked') == 1) {
344→        DATOS.append('fecha_vencimiento_agendas', $('#fecha_vencimiento_agendas1').val());
345→        DATOS.delete('dias_vencimiento_agendas');
346→    } else if ($('#es_dias1').prop('checked') == 1) {
347→        DATOS.append('dias_vencimiento_agendas', $('#dias_vencimiento_agendas1').val());
348→        DATOS.delete('fecha_vencimiento_agendas');
349→    }
350→
351→    DATOS.append('capturar_camara', $('#capturar_camara_reenviar').prop('checked') ? 1 : 0);
352→    DATOS.append('capturar_video', $('#capturar_video_reenviar').prop('checked') ? 1 : 0);
353→
354→    if (ID !== null && participantes_seleccionados.length === 0) {
355→        participantes_seleccionados = [ID];
356→    }
357→
358→    DATOS.append('participantes', JSON.stringify(participantes_seleccionados));
359→    DATOS.append('pruebas', JSON.stringify($('#pruebas_reenviar').val()));
360→    DATOS.append('empresa_id', dato_empresa?.id);
361→
362→    await $.ajax({
363→        url        : `/participantes-reagendar`,
364→        type       : 'POST',
365→        data       : DATOS,
366→        contentType: false,
367→        processData: false
368→    }).done(function (respuesta) {
369→        limpiarFormularioReenviarAgendamiento();
370→        habilitarBotonesAcciones();
371→        $('#modal-reagendar').modal('hide');
372→
373→        mostrarToast('success', respuesta.message);
374→
375→        if (respuesta.advertencias && respuesta.advertencias.length > 0) {
376→            mostrarModalAdvertenciasReagendamiento(respuesta);
377→        }
378→
379→        cargarListadoParticipantes();
380→    }).fail(function (error) {
381→        let msg = 'Error al reenviar agendamiento';
382→        if (error && error.responseJSON) {
383→            const e = error.responseJSON;
384→            if (e.errors !== undefined && e.errors !== null) {
385→                if (typeof e.errors === 'string') {
386→                    msg = e.errors;
387→                } else if (Array.isArray(e.errors)) {
388→                    msg = e.errors.join(' ');
389→                } else if (typeof e.errors === 'object') {
390→                    try {
391→                        const vals = Object.values(e.errors).map(v => Array.isArray(v) ? v.join(' ') : v);
392→                        msg = vals.join(' ');
393→                    } catch (_) {}
394→                }
395→            } else if (e.message) {
396→                msg = e.message;
397→            }
398→        } else if (error && error.message) {
