412→function limpiarFormularioReenviarAgendamiento() {
413→    participantes_seleccionados = [];
414→    $('#email_reenviar').val(null);
415→    $('#pruebas_reenviar').val([]).trigger('change');
416→
417→    if (PROCESO.dias_vencimiento_agendas) {
418→        // CASO 1: El proceso usa días - mantener días
419→        $('#es_fecha1').prop('checked', false).trigger('change');
420→        $('#fecha_vencimiento_agendas1').val(null);
421→        $('#es_dias1').prop('checked', true).trigger('change');
422→        $('#dias_vencimiento_agendas1').val(PROCESO.dias_vencimiento_agendas);
423→    } else if (PROCESO.fecha_vencimiento_agendas) {
424→        // CASO 2: El proceso usa fecha específica
425→        $('#es_dias1').prop('checked', false).trigger('change');
426→        $('#dias_vencimiento_agendas1').val(null);
427→        $('#es_fecha1').prop('checked', true).trigger('change');
428→
429→        // Verificar si la fecha del proceso es futura o pasada
430→        const fecha_proceso = new Date(PROCESO.fecha_vencimiento_agendas);
431→        const fecha_actual = new Date();
432→
433→        if (fecha_proceso > fecha_actual) {
434→            // Fecha futura: usar la fecha del proceso
435→            $('#fecha_vencimiento_agendas1').val(PROCESO.fecha_vencimiento_agendas);
436→        } else {
437→            // Fecha pasada: usar 1 día desde hoy
438→            const fecha_futura = new Date();
439→            fecha_futura.setDate(fecha_futura.getDate() + 1);
440→            const fecha_futuraISO = fecha_futura.toISOString().slice(0, 16);
441→            $('#fecha_vencimiento_agendas1').val(fecha_futuraISO);
442→        }
443→    }
444→
445→    $('#capturar_camara_reenviar').prop('checked', PROCESO.capturar_camara);
446→    $('#capturar_video_reenviar').prop('checked', PROCESO.capturar_video);
447→}
