        DATOS.delete('dias_vencimiento_agendas');
    } else if ($('#es_dias1').prop('checked') == 1) {
        DATOS.append('dias_vencimiento_agendas', $('#dias_vencimiento_agendas1').val());
        DATOS.delete('fecha_vencimiento_agendas');
    }

    DATOS.append('capturar_camara', $('#capturar_camara_reenviar').prop('checked') ? 1 : 0);
