    await $.ajax({
        url        : `/participantes-reagendar`,
        type       : 'POST',
        data       : DATOS,
        contentType: false,
        processData: false
    }).done(function (respuesta) {
        limpiarFormularioReenviarAgendamiento();
        habilitarBotonesAcciones();
        $('#modal-reagendar').modal('hide');

        mostrarToast('success', respuesta.message);

        if (respuesta.advertencias && respuesta.advertencias.length > 0) {
            mostrarModalAdvertenciasReagendamiento(respuesta);
        }

        cargarListadoParticipantes();
    }).fail(function (error) {
        let msg = 'Error al reenviar agendamiento';
        if (error && error.responseJSON) {
            const e = error.responseJSON;
            if (e.errors !== undefined && e.errors !== null) {
                if (typeof e.errors === 'string') {
                    msg = e.errors;
                } else if (Array.isArray(e.errors)) {
                    msg = e.errors.join(' ');
                } else if (typeof e.errors === 'object') {
                    try {
                        const vals = Object.values(e.errors).map(v => Array.isArray(v) ? v.join(' ') : v);
                        msg = vals.join(' ');
                    } catch (_) {}
                }
            } else if (e.message) {
                msg = e.message;
            }
        } else if (error && error.message) {
            msg = error.message;
        }
        mostrarToast('danger', msg);
    });
}

