     2→
     3→/**
     4→ * Codifica una cadena en base64 con alfabeto URL-safe y sin padding.
     5→ *
     6→ * @param string $data Cadena original
     7→ * @return string Cadena codificada lista para usarse en tokens
     8→ * @author Sergio Vargas
     9→ */
    10→function sso_base64url_encode(string $data): string {
    11→    return rtrim(strtr(base64_encode($data), '+/', '-_'), '=');
    12→}
    13→
    14→/**
    15→ * Decodifica una cadena en base64 con alfabeto URL-safe.
    16→ *
    17→ * @param string $data Cadena codificada
    18→ * @return string Cadena decodificada
    19→ * @author Sergio Vargas
    20→ */
    21→function sso_base64url_decode(string $data): string {
    22→    $remainder = strlen($data) % 4;
    23→    if ($remainder) $data .= str_repeat('=', 4 - $remainder);
    24→    return base64_decode(strtr($data, '-_', '+/'));
    25→}
    26→
    27→/**
    28→ * Firma un payload simple al estilo JWT usando HMAC SHA-256.
    29→ *
    30→ * @param array $payload Datos del usuario/solicitud a encapsular
    31→ * @param string $secret Llave secreta compartida entre los servicios
    32→ * @return string Token SSO listo para enviar
    33→ * @author Sergio Vargas
    34→ */
    35→function sso_sign_token(array $payload, string $secret): string {
    36→    $header = ['alg' => 'HS256', 'typ' => 'SSO'];
    37→    $h = sso_base64url_encode(json_encode($header));
    38→    $p = sso_base64url_encode(json_encode($payload));
    39→    $sig = hash_hmac('sha256', "$h.$p", $secret, true);
    40→    $s = sso_base64url_encode($sig);
    41→    return "$h.$p.$s";
    42→}
    43→
    44→/**
    45→ * Verifica la firma del token SSO y devuelve el payload si es valido.
    46→ *
    47→ * @param string $token Token recibido en el cliente destino
    48→ * @param string $secret Llave secreta compartida
    49→ * @return array|null Payload del token si es valido, null si falla la firma
    50→ * @author Sergio Vargas
    51→ */
    52→function sso_verify_token(string $token, string $secret): ?array {
    53→    $parts = explode('.', $token);
    54→    if (count($parts) !== 3) return null;
    55→    [$h, $p, $s] = $parts;
    56→
    57→    $expected = sso_base64url_encode(hash_hmac('sha256', "$h.$p", $secret, true));
    58→    if (!hash_equals($expected, $s)) return null;
    59→
    60→    $payloadJson = sso_base64url_decode($p);
    61→    $payload = json_decode($payloadJson, true);
    62→    return is_array($payload) ? $payload : null;
    63→}
