    18→/**
    19→ * Cargar etapas de la vacante con sus candidatos desde el endpoint.
    20→ *
    21→ * @param {Object} filtros Objeto con filtros a aplicar (opcional).
    22→ * @returns {void}
    23→ * @author Andres Morales
    24→ * @author Andres Gonzalez
    25→ */
    26→function cargarEtapasConCandidatos(filtros = {}) {
    27→    // Guardar posición del scroll antes de actualizar (solo si no es la primera carga)
    28→    if (!es_primera_carga) {
    29→        var $contenedor = $('#contenido-etapas-lista');
    30→        if ($contenedor.length) {
    31→            posicion_scroll_guardada = $contenedor.scrollLeft();
    32→        }
    33→    }
    34→
    35→    $.ajax({
    36→        url: `/etapas-vacantes-aplicantes/${VACANTE_ID}`,
    37→        type: 'GET',
    38→        dataType: 'json',
    39→        data: {
    40→            ciudad_id: filtros.ciudad_id || '',
    41→            departamento_id: filtros.departamento_id || '',
    42→            pais_id: filtros.pais_id || '',
    43→            etapa_id: filtros.etapa_id || '',
    44→            nivel_educativo_id: filtros.nivel_educativo_id || '',
    45→            fecha_desde: filtros.fecha_desde || '',
    46→            fecha_hasta: filtros.fecha_hasta || '',
    47→            edad: filtros.edad || '',
    48→            chips: filtros.chips,
    49→            nivel_adecuacion: filtros.nivel_adecuacion || ''
    50→        },
    51→        success: function(respuesta) {
    52→            let datos = respuesta.data;
    53→            renderizarEtapas(datos.etapas, calcularTotalCandidatos(datos.etapas) + datos.descartados.length);
    54→            actualizarConteoDescartados(datos.descartados.length);
    55→
    56→            candidatos_descartados = datos.descartados;
    57→        },
    58→        error: function() {
    59→            mostrarToast('danger', 'Error al cargar las etapas y candidatos');
    60→        }
    61→    });
    62→}
