   187→/**
   188→ * Renderizar las etapas de la vacante en cards horizontales.
   189→ *
   190→ * @param {Array} etapas Array de etapas de la vacante.
   191→ * @param {number} conteo_aplicantes Cantidad de aplicantes de la vacante.
   192→ * @returns {void}
   193→ * @author Andres Morales
   194→ * @author Andres Gonzalez
   195→ * @author Jair Carvajal
   196→ */
   197→function renderizarEtapas(etapas, conteo_aplicantes) {
   198→    var $contenido_lista = $('#contenido-etapas-lista');
   199→    var $contenido_vacio = $('#contenido-etapas-empty');
   200→    var $contenido_controles = $('#contenido-etapas-controles');
   201→
   202→    // Verificar si hay filtros activos
   203→    var hay_filtros_activos = Object.keys(json_filtros_etapas).length > 0;
   204→
   205→    // Solo mostrar empty state si NO hay aplicantes Y NO hay filtros activos
   206→    if ((!conteo_aplicantes || conteo_aplicantes === 0) && !hay_filtros_activos) {
   207→        $contenido_lista.addClass('d-none');
   208→        $contenido_controles.addClass('d-none');
   209→        $contenido_vacio.removeClass('d-none');
   210→        return;
   211→    }
   212→
   213→    $contenido_vacio.addClass('d-none');
   214→    $contenido_lista.removeClass('d-none');
   215→    $contenido_controles.removeClass('d-none');
   216→
   217→    if (!etapas || etapas.length === 0) {
   218→        $contenido_lista.html(`
   219→            <div class="w-100 text-center py-5">
   220→                <p class="text-muted">${hay_filtros_activos ? 'No se encontraron coincidencias con los filtros aplicados. vuelve a intentar con otros filtros' : 'No hay etapas configuradas para esta vacante'}</p>
   221→            </div>
   222→        `);
   223→        return;
   224→    }
   225→
   226→    let html = '';
   227→    etapas.sort((a, b) => a.orden - b.orden);
   228→
   229→    // Filtrar la etapa con el orden inmediatamente anterior al orden 8 (Contratados)
   230→    let etapa_anterior_orden_8 = etapas
   231→        .filter(etapa => etapa.orden < 8)
   232→        .sort((a, b) => b.orden - a.orden)[0];
   233→
   234→    etapas.forEach((etapa) => {
   235→        html += crearTarjetaEtapa(etapa, etapa_anterior_orden_8);
   236→    });
   237→
   238→    $contenido_lista.html(html);
   239→
   240→    cargarEtapasEnFiltro(etapas);
   241→
   242→    // Ejecutar animación solo en la primera carga, de lo contrario restaurar posición
   243→    if (es_primera_carga) {
   244→        animarScrollEtapas();
   245→        es_primera_carga = false;
   246→    } else {
   247→        // Restaurar la posición del scroll guardada
   248→        setTimeout(() => {
   249→            $contenido_lista.scrollLeft(posicion_scroll_guardada);
   250→        }, 50);
   251→    }
   252→
   253→    inicializarEventosCheckboxes();
   254→
   255→    // Inicializar tooltips después de renderizar
   256→    setTimeout(() => {
   257→        inicializarTooltips();
   258→    }, 100);
   259→}
