   261→/**
   262→ * Crear una tarjeta(card) de etapa.
   263→ *
   264→ * @param {Object} etapa Datos de la etapa.
   265→ * @param {Object} etapa_anterior_orden_8 Etapa con el orden inmediatamente anterior al orden 8.
   266→ * @returns {string} HTML de la card.
   267→ * @author Andres Morales
   268→ * @author Andres Gonzalez
   269→ * @author Jair Carvajal
   270→ * @author Esperanza Olivo
   271→ */
   272→function crearTarjetaEtapa(etapa, etapa_anterior_orden_8) {
   273→    let etapa_anterior_contracion = false;
   274→
   275→    if (etapa_anterior_orden_8 && etapa.orden === etapa_anterior_orden_8.orden && etapa.activa == 1) {
   276→        etapa_anterior_contracion = true;
   277→    }
   278→
   279→    // Detectar si es etapa de prueba técnica sin archivos
   280→    let es_prueba_tecnica = etapa.nombre === 'Prueba técnica';
   281→    let tiene_prueba_tecnica_cargada = etapa?.prueba_cargada ?? true;
   282→
   283→    let candidatos_html = '';
   284→    let candidatos_totales = 0;
   285→    let candidatos_completaron = 0;
   286→
   287→    // Mostrar mensaje si es prueba técnica sin archivos
   288→    if (es_prueba_tecnica && !tiene_prueba_tecnica_cargada) {
   289→        let html = crearMensajeSinArchivosInstrucciones(etapa.id);
   290→
   291→        // Si hay candidatos, agregarlos después del mensaje
   292→        if (etapa?.usuarios_vacantes?.length > 0) {
   293→            etapa.usuarios_vacantes.forEach((usuario_vacante) => {
   294→                html += crearCardCandidato(usuario_vacante, 'listado', etapa.nombre, etapa_anterior_contracion);
   295→            });
   296→            candidatos_totales = etapa.usuarios_vacantes.length;
   297→            candidatos_completaron = etapa.usuarios_vacantes.filter((usuario_vacante) => usuario_vacante.puntaje !== null || usuario_vacante.puntaje_ia !== null).length;
   298→        }
   299→
   300→        candidatos_html = `<div class="candidatos-container">${html}</div>`;
   301→    } else if (etapa?.usuarios_vacantes?.length > 0) {
   302→        let html = '';
   303→
   304→        etapa.usuarios_vacantes.forEach((usuario_vacante) => {
   305→            html += crearCardCandidato(usuario_vacante, 'listado', etapa.nombre, etapa_anterior_contracion);
   306→        });
   307→
   308→        candidatos_html = `<div class="candidatos-container">${html}</div>`;
   309→        candidatos_totales = etapa.usuarios_vacantes.length;
   310→        candidatos_completaron = etapa.nombre === 'Documentos para contratación'
   311→            ? etapa.usuarios_vacantes.filter((usuario_vacante) => usuario_vacante.etapa_completada === true).length
   312→            : etapa.usuarios_vacantes.filter((usuario_vacante) => usuario_vacante.puntaje !== null || usuario_vacante.puntaje_ia !== null).length;
   313→    } else {
   314→        candidatos_html = `
   315→            <div class="candidatos-container">
   316→                <div class="mensaje-etapa-vacia d-flex justify-content-center align-items-center my-5"><div class="etapa-icono-circulo"><i class="bi bi-arrow-90deg-right"></i></div></div>
   317→                <div class="mensaje-etapa-vacia mt-auto pb-10 px-5"><p class="text-headline-5 text-gray-40 text-center mb-5 pb-10 px-6">Mueve candidatos con el botón o arrástralos desde la etapa anterior. </p></div>
   318→            </div>
   319→        `;
   320→    }
   321→    return `
   322→        <div class="card etapa-card bg-gray-90" data-etapa-id="${etapa.id}">
   323→            <div class="card-body d-flex flex-column">
   324→                <div class="d-flex align-items-center mb-3 gap-3">
   325→                    ${crearCheckbox({
   326→                        id: 'etapa-check-' + etapa.id,
   327→                        name: 'etapa_check',
   328→                        value: etapa.id,
   329→                        checked: false,
   330→                        input_container_class: 'd-inline-block',
   331→                        disabled: (candidatos_totales > 0) ? false : true
   332→                    })}
   333→                    <h6 class="etapa-titulo text-headline-5 fw-bold mb-0">${etapa.nombre}</h6>
   334→                    ${(candidatos_totales > 0 && etapa.nombre !== 'Contratados') ?
   335→                        `<div class="ms-auto">
   336→                            ${crearTag({
   337→                                texto: candidatos_completaron + '/' + candidatos_totales + ' completaron la tarea',
   338→                                class: etapa.nombre === 'Documentos para contratación' ? 'tag-tonal-green' : 'tag-tonal-blue',
   339→                            })}
   340→                        </div>`
   341→                    : ''}
   342→                </div>
   343→                ${candidatos_html}
   344→            </div>
   345→        </div>
   346→    `;
   347→}
