   393→/**
   394→ * Crear una card individual de candidato.
   395→ *
   396→ * @param {Object} usuario_vacante Datos del candidato aplicante.
   397→ * @param {string} origen Origen del render: 'listado' o 'modal'. Default: 'listado'
   398→ * @param {string} nombre_etapa Nombre de la etapa actual. Default: null
   399→ * @returns {string} HTML de la card del candidato.
   400→ * @author Andres Morales
   401→ * @author Andres Gonzalez
   402→ * @author José Manuel Jiménez
   403→ * @author Jair Carvajal
   404→ */
   405→function crearCardCandidato(usuario_vacante, origen = 'listado', nombre_etapa = null, etapa_anterior_contracion = false) {
   406→    var usuario = usuario_vacante.usuario || {};
   407→    var nombre_completo = usuario.nombre_completo?.trim() || 'Sin nombre';
   408→    var salario = usuario?.perfiles_laborales[0]?.salario || 0;
   409→    var salario_maximo = usuario?.perfiles_laborales[0]?.salario_maximo || 0;
   410→
   411→    var salario_solicitado = (salario_maximo > 0) ? `Salario: ${formatearSalario(salario)} a ${formatearSalario(salario_maximo)}` : `Solicita ${formatearSalario(salario)}`;
   412→    var experiencia_meses = usuario?.perfiles_laborales[0]?.experiencia_laboral_meses || 0;
   413→    var experiencia = convertirMesesAAnios(experiencia_meses);
   414→
   415→    var ciudad = usuario.ciudad?.nombre || '';
   416→    var pais = usuario.ciudad?.departamento?.pais?.nombre || '';
   417→    var ubicacion = `${ciudad}${ciudad && pais ? ',' : ''}${pais}`;
   418→
   419→    var documento = usuario.documento || '';
   420→
   421→    var adecuacion = usuario_vacante.adecuacion;
   422→    var borde_color = '';
   423→    var etiqueta_estilo = '';
   424→    if (adecuacion >= 10 && adecuacion <= 40) {
   425→        borde_color = 'borde-indice-talento-rojo';
   426→        etiqueta_estilo = 'badge-indice-talento-rojo';
   427→    } else if (adecuacion >= 41 && adecuacion <= 70) {
   428→        borde_color = 'borde-indice-talento-amarillo';
   429→        etiqueta_estilo = 'badge-indice-talento-amarillo';
   430→    } else if (adecuacion >= 71 && adecuacion <= 100) {
   431→        borde_color = 'borde-indice-talento-verde';
   432→        etiqueta_estilo = 'badge-indice-talento-verde';
   433→    }
   434→
   435→    var tiene_motivo_abandono = origen === 'modal' && usuario_vacante.etapa_descarte?.motivo_abandono !== null;
   436→
   437→    var checkbox_html = !tiene_motivo_abandono ?
   438→        crearCheckbox({
   439→            id: `candidato-check-${usuario_vacante.id}`,
   440→            name: `candidato_check`,
   441→            value: usuario_vacante.id,
   442→            class: 'candidato-checkbox',
   443→            input_container_class: 'd-inline-block'
   444→        }) : '';
   445→
   446→    var etapa_descarte_html = origen === 'modal' && usuario_vacante.etapa_descarte ?
   447→        `${crearTag({
   448→            texto: `Etapa de descarte: ${usuario_vacante.etapa_descarte.nombre}`,
   449→            class: 'tag-tonal-orange w-fit-content',
   450→        })}` : '';
   451→
   452→    var fecha_descarte = '';
   453→    if (origen === 'modal' && usuario_vacante.created_at) {
   454→        var fecha = new Date(usuario_vacante.created_at);
   455→        var dia = fecha.getDate();
   456→        var mes = fecha.toLocaleDateString('es-CO', { month: 'short' }).replace('.', '');
   457→        var anio = fecha.getFullYear();
   458→        fecha_descarte = `${dia}/${mes.charAt(0).toUpperCase() + mes.slice(1)}/${anio}`;
   459→    }
   460→
   461→    return `
   462→        <div class="card candidato-card mb-3" data-candidato-id="${usuario.id}" data-etapa-vacante-usuario-id="${usuario_vacante.id}" data-tiene-pruebas-requeridas="${usuario_vacante.tiene_pruebas_requeridas || false}">
   463→            <div class="card-body p-3">
   464→                <div class="d-flex align-items-center gap-1">
   465→                    ${checkbox_html}
   466→                    <div class="candidato-avatar-container d-flex flex-column align-items-center justify-content-center text-center">
   467→                        <div class="position-relative d-inline-block">
   468→                            <img class="candidato-avatar ${borde_color}"
   469→                                onclick="verDetalleCandidato(${usuario.id})"
   470→                                src="/usuarios-imagen/${usuario.id}"
   471→                                ${crearTooltip({
   472→                                    placement: `bottom`,
   473→                                    content: `Ver detalle del candidato`,
   474→                                    toggle: `popover`,
   475→                                })}>
   476→
   477→                            ${(adecuacion !== null && adecuacion > 0) ? `
   478→                                <span class="candidato-badge-detalle text-label-1 badge ${etiqueta_estilo} position-absolute">
   479→                                    ${adecuacion ? `${adecuacion}%` : ''}
   480→                                </span>
   481→                            ` : ''}
   482→                        </div>
   483→
   484→                        ${ubicacion ? `
   485→                            <div class="d-flex align-items-center gap-2 mt-3">
   486→                                <span class="text-label-2 text-gray-30 mt-1">${ubicacion}</span>
   487→                            </div>
   488→                        ` : ''}
   489→                    </div>
   490→                    <div class="flex-grow-1">
   491→                        <h6 class="candidato-nombre mb-2 text-headline-6 fw-bold text-gray-10" onclick="verDetalleCandidato(${usuario.id})">${nombre_completo}</h6>
   492→                        <div class="candidato-info d-flex flex-column gap-1">
   493→                            ${etapa_descarte_html}
   494→                            ${(origen === 'modal' && fecha_descarte) ? `
   495→                                <div class="d-flex align-items-center gap-2">
   496→                                    <i class="bi bi-calendar-event"></i>
   497→                                    <span class="text-label-2">Fecha de descarte: ${fecha_descarte}</span>
   498→                                </div>
   499→                            ` : ''}
   500→                            ${documento ? `
   501→                                <div class="d-flex align-items-center gap-2">
   502→                                    <img width="12" heigth="16" src="/iconos/file-person.png">
   503→                                    <span class="text-label-2">No. documento: ${documento}</span>
   504→                                </div>
   505→                            ` : ''}
   506→                            ${experiencia ? `
   507→                                <div class="d-flex align-items-center gap-2">
   508→                                    <i class="bi bi-briefcase"></i>
   509→                                    <span class="text-label-2">Experiencia: ${experiencia}</span>
   510→                                </div>
   511→                            ` : ''}
   512→                            ${salario_solicitado ? `
   513→                                <div class="d-flex align-items-center gap-2">
   514→                                    <i class="bi bi-currency-dollar"></i>
   515→                                    <span class="text-label-2">${salario_solicitado}</span>
   516→                                </div>
   517→                            ` : ''}
   518→                        </div>
   519→                    </div>
   520→
   521→                    <div class="d-flex flex-column align-items-end gap-2">
   522→                        ${generarBotonesAccionCandidato(usuario_vacante, origen, nombre_etapa, etapa_anterior_contracion, tiene_motivo_abandono)}
   523→                    </div>
   524→                </div>
   525→            </div>
   526→        </div>
   527→    `;
   528→}
