   958→/**
   959→ * Ejecutar el movimiento de etapa (flujo normal).
   960→ *
   961→ * @param {number} etapa_vacante_usuario_id ID de la relación.
   962→ * @returns {void}
   963→ * @author Sergio Vargas
   964→ * @author Andres Gonzalez
   965→ * @author Jair Carvajal
   966→ * @author Andres Morales
   967→ */
   968→function ejecutarMovimientoEtapa(etapa_vacante_usuario_id) {
   969→    var botones_mover_etapa = $(`.btn-mover-etapa-accion[data-etapa-vacante-usuario-id="${etapa_vacante_usuario_id}"]`);
   970→    botones_mover_etapa.prop('disabled', true);
   971→
   972→    var modal_detalle_abierto = $('#modal-detalle-resultados').hasClass('show');
   973→
   974→    if (modal_detalle_abierto) {
   975→        moverVacantesEtapaAplicante(etapa_vacante_usuario_id);
   976→        return;
   977→    }
   978→
   979→    var seccion_candidato = $(`.candidato-card[data-etapa-vacante-usuario-id="${etapa_vacante_usuario_id}"]`);
   980→
   981→    if (seccion_candidato.length > 0) {
   982→        var etapa_actual = seccion_candidato.closest('.etapa-card');
   983→        var etapa_siguiente = etapa_actual.next('.etapa-card');
   984→
   985→        if (etapa_siguiente.length > 0) {
   986→            var $contenedor_etapas = $('#contenido-etapas-lista');
   987→            var posicion_original = seccion_candidato.offset();
   988→            var ancho_original = seccion_candidato.outerWidth();
   989→            var alto_original = seccion_candidato.outerHeight();
   990→
   991→            var contenedor_candidatos_siguiente = etapa_siguiente.find('.candidatos-container');
   992→            if (contenedor_candidatos_siguiente.length === 0) {
   993→                contenedor_candidatos_siguiente = etapa_siguiente.find('.card-body');
   994→            }
   995→            var posicion_siguiente = contenedor_candidatos_siguiente.offset();
   996→            var ancho_contenedor = contenedor_candidatos_siguiente.outerWidth();
   997→            var margen = 85;
   998→
   999→            var izquierda_centrado = posicion_siguiente.left + (ancho_contenedor / 2) - (ancho_original / 2);
  1000→            var arriba_margen = posicion_siguiente.top + margen;
  1001→
  1002→            var posicion_contenedor = $contenedor_etapas.offset();
  1003→            var arriba_contenedor = posicion_contenedor.top;
  1004→            var izquierda_contenedor = posicion_contenedor.left;
  1005→            var ancho_contenedor_pixel = $contenedor_etapas.outerWidth();
  1006→            var alto_contenedor_pixel = $contenedor_etapas.outerHeight();
  1007→
  1008→            var envolvente_animacion = $('<div id="wrapper-animacion-candidato"></div>').css({
  1009→                position: 'fixed',
  1010→                top: arriba_contenedor + 'px',
  1011→                left: izquierda_contenedor + 'px',
  1012→                width: ancho_contenedor_pixel + 'px',
  1013→                height: alto_contenedor_pixel + 'px',
  1014→                overflow: 'hidden',
