  1311→ * @author José Manuel Jiménez
  1312→ */
  1313→function generarBotonesAccionCandidato(usuario_vacante, origen, nombre_etapa, etapa_anterior_contracion, tiene_motivo_abandono) {
  1314→    let html = '';
  1315→
  1316→    // Lógica para etapa de Prueba técnica
  1317→    if (origen === 'listado' && nombre_etapa === 'Prueba técnica') {
  1318→        let prueba_tecnica = usuario_vacante.prueba_tecnica || {};
  1319→        let tiene_prueba = prueba_tecnica.existe && (prueba_tecnica.tiene_archivos || prueba_tecnica.tiene_enlace);
  1320→        let tiene_calificacion = usuario_vacante.puntaje !== null;
  1321→
  1322→        if (!tiene_prueba) {
  1323→            // Ojo gris - candidato NO ha subido su prueba
  1324→            html += crearBoton({
  1325→                clases: 'btn-outline-secondary btn-circle',
  1326→                texto: '<i class="bi bi-eye-slash"></i>',
  1327→                disabled: true,
  1328→                tooltip: crearTooltip({
  1329→                    content: 'Candidato no ha subido su prueba técnica',
  1330→                    placement: 'bottom'
  1331→                })
  1332→            });
  1333→        } else if (!tiene_calificacion) {
  1334→            // Ojo azul - abre detalle del candidato para calificar
  1335→            html += crearBoton({
  1336→                clases: 'btn-outline-primary btn-circle',
  1337→                texto: '<i class="bi bi-eye"></i>',
  1338→                onclick: `verDetalleCandidato(${usuario_vacante.usuario.id}, 'Prueba técnica')`,
  1339→                tooltip: crearTooltip({
  1340→                    content: 'Ver detalle del candidato',
  1341→                    placement: 'bottom'
  1342→                })
  1343→            });
  1344→        } else {
  1345→            // Flecha - puede mover de etapa
  1346→            html += crearBoton({
  1347→                clases: 'btn-outline-primary btn-circle btn-mover-etapa-accion',
  1348→                adicional: `data-etapa-vacante-usuario-id="${usuario_vacante.id}"`,
  1349→                texto: '<i class="bi bi-arrow-90deg-right"></i>',
  1350→                onclick: `moverEtapaCandidato(${usuario_vacante.id})`,
  1351→                tooltip: crearTooltip({
  1352→                    content: 'Mover de etapa',
  1353→                    placement: 'bottom'
  1354→                })
  1355→            });
  1356→        }
  1357→    } else if (origen === 'listado' && nombre_etapa !== 'Contratados') {
  1358→        // Lógica para otras etapas (código existente)
  1359→        let icono = '<i class="bi bi-arrow-90deg-right"></i>';
  1360→        let mensaje = 'Mover de etapa';
  1361→        if (etapa_anterior_contracion) {
  1362→            icono = '<i class="bi bi-file-earmark-person"></i>';
  1363→            mensaje = 'Seleccionar como contratado';
  1364→        }
  1365→
  1366→        html += crearBoton({
  1367→            clases: 'btn-outline-primary btn-circle btn-mover-etapa-accion',
  1368→            adicional: `data-etapa-vacante-usuario-id="${usuario_vacante.id}"`,
  1369→            texto: icono,
  1370→            onclick: `moverEtapaCandidato(${usuario_vacante.id})`,
  1371→            tooltip: crearTooltip({
  1372→                content: mensaje,
  1373→                placement: 'bottom'
  1374→            }),
  1375→            disabled: nombre_etapa === 'Documentos para contratación'
  1376→                ? !usuario_vacante.etapa_completada
  1377→                : ((usuario_vacante.puntaje === null && usuario_vacante.puntaje_ia === null) || usuario_vacante.etapa_completada === false || (usuario_vacante?.formato_entrevista_completado !== undefined && usuario_vacante?.formato_entrevista_completado === false))
  1378→        });
  1379→    }
  1380→
  1381→    // Botón de descartar
  1382→    if (origen === 'listado' && nombre_etapa !== 'Contratados') {
  1383→        html += crearBoton({
  1384→            clases: 'btn-outline-danger btn-circle',
  1385→            texto: `<i class="bi bi-person-x"></i>`,
  1386→            onclick: `descartarCandidatos(${usuario_vacante.id})`,
  1387→            tooltip: crearTooltip({
  1388→                content: 'Descartar candidato',
  1389→                placement: 'bottom'
  1390→            })
  1391→        });
  1392→    }
  1393→
  1394→    // Botón de revertir descarte (para modal)
  1395→    if (origen === 'modal') {
  1396→        html += crearBoton({
  1397→            clases: 'btn-outline-success btn-circle',
  1398→            texto: `<i class="bi bi-person-add"></i>`,
  1399→            onclick: `devolverDescarteCandidato(${usuario_vacante.id})`,
  1400→            tooltip: crearTooltip({
