  1672→function quitarArchivoInstruccion(id) {
  1673→    // Buscar la imagen en el DOM y revocar la URL temporal si existe
  1674→    let img = $(`img[src]`).filter(function() {
  1675→        return $(this).closest('.archivo-item').find(`[onclick*="${id}"]`).length > 0;
